class Auth {
  constructor() {
    this.authProtected = true;
  }

  login() {
    this.authProtected = false;
    localStorage.setItem("auth", this.authProtected);
  }

  logout(cb) {
    this.authProtected = true;
    localStorage.setItem("auth", this.authProtected);
    cb();
  }

  isAuthProtected() {
    this.authProtected = localStorage.getItem("auth");
    return this.authProtected;
  }
}

export default new Auth();
