import React, { useState } from "react";

import {
  Row,
  Col,
  Button,
  Container,
  Label,
  UncontrolledAlert,
} from "reactstrap";

// Redux

import { Link, Redirect } from "react-router-dom";
import AdminUserPool from "./AdminUserPool";

// availity-reactstrap-validation
import { AvForm, AvField } from "availity-reactstrap-validation";

// import images
import logodark from "../../assets/images/logo-dark.png";
import logolight from "../../assets/images/logo-light.png";

import "./RegisterAdmin.scss";

import { CognitoUser, AuthenticationDetails } from "amazon-cognito-identity-js";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [adminLoggedIn, setAdminLoggedIn] = useState();
  const [redirect, setRedirect] = useState(false);
  const [step, setStep] = useState(0);
  const [userType, setUserType] = useState("");
  const [resetEmail, setResetEmail] = useState("");
  const [code, setCode] = useState("");
  const [otpSuccess, setOtpSuccess] = useState(false);

  const adminLogin = (event) => {
    event.preventDefault();

    const adminUser = new CognitoUser({
      Username: email,
      Pool: AdminUserPool,
    });

    const adminAuthDetails = new AuthenticationDetails({
      Username: email,
      Password: password,
    });

    adminUser.authenticateUser(adminAuthDetails, {
      onSuccess: (data) => {
        setAdminLoggedIn(true);
      },
      onFailure: (err) => {
        setAdminLoggedIn(false);
      },
      newPasswordRequired: (data) => {
        console.log("Password rquired");
      },
    });
  };

  const sendAdminCode = (event) => {
    event.preventDefault();
  };

  return (
    <React.Fragment>
      <div>
        <Container fluid className="p-0">
          <Row className="g-0">
            <Col lg={4}>
              <div className="authentication-page-content p-4 d-flex align-items-center min-vh-100">
                <div className="w-100">
                  <Row className="justify-content-center">
                    <Col lg={9}>
                      <div>
                        <div className="text-center">
                          <div>
                            <Link to="/" class="">
                              <img
                                src={logodark}
                                alt=""
                                height="20"
                                class="auth-logo logo-dark mx-auto"
                              />
                              <img
                                src={logolight}
                                alt=""
                                height="20"
                                class="auth-logo logo-light mx-auto"
                              />
                            </Link>
                          </div>

                          <h4 className="font-size-18 mt-4">Welcome Back!</h4>
                          <p className="text-muted">
                            Sign in to continue to PetPawnions.
                          </p>
                        </div>
                        {step === 0 ? (
                          <h1>
                            <div
                              className="user-type"
                              style={{
                                display: "flex",
                                flexDirection: "column",
                              }}
                            >
                              <Button
                                color="success"
                                className="w-md waves-effect waves-light"
                                type="submit"
                                onClick={() => {
                                  setUserType("Admin");
                                  setStep(1);
                                }}
                              >
                                Log In as an Admin
                              </Button>
                              <h2
                                style={{
                                  width: "100%",
                                  textAlign: "center",
                                  borderBottom: "1px solid #000",
                                  lineHeight: "0.1em",
                                  margin: "20px 0 20px",
                                }}
                              >
                                <span
                                  style={{
                                    backgroundColor: "#fff",
                                    padding: "0 15px",
                                    fontSize: "15px",
                                  }}
                                >
                                  OR
                                </span>
                              </h2>
                              <Button
                                color="primary"
                                className="w-md waves-effect waves-light"
                                type="submit"
                                onClick={() => setUserType("Author")}
                              >
                                Log In as an Author
                              </Button>
                            </div>
                          </h1>
                        ) : null}
                        {step === 1 ? (
                          <div className="login-flow">
                            {adminLoggedIn ? (
                              <div>
                                {setTimeout(() => {
                                  setRedirect(true);
                                }, 2000)}
                                {redirect ? <Redirect to="/dashboard" /> : null}
                              </div>
                            ) : null}
                            {adminLoggedIn === false ? (
                              <UncontrolledAlert
                                color="danger"
                                className="alert-dismissible fade show"
                                role="alert"
                              >
                                <i className="mdi mdi-alert-outline me-2"></i>
                                Authentication failed! Please try again
                              </UncontrolledAlert>
                            ) : null}

                            <div className="p-2 mt-5">
                              <AvForm className="form-horizontal">
                                <div className="auth-form-group-custom mb-4">
                                  <i className="ri-user-2-line auti-custom-input-icon"></i>
                                  <Label htmlFor="username">Email</Label>
                                  <AvField
                                    name="username"
                                    value={email}
                                    type="text"
                                    className="form-control"
                                    id="username"
                                    validate={{ email: true, required: true }}
                                    placeholder="Enter username"
                                    onChange={(e) => setEmail(e.target.value)}
                                  />
                                </div>

                                <div className="auth-form-group-custom mb-4">
                                  <i className="ri-lock-2-line auti-custom-input-icon"></i>
                                  <Label htmlFor="userpassword">Password</Label>
                                  <AvField
                                    name="password"
                                    value={password}
                                    type="password"
                                    className="form-control"
                                    id="userpassword"
                                    placeholder="Enter password"
                                    onChange={(e) =>
                                      setPassword(e.target.value)
                                    }
                                  />
                                </div>

                                <div
                                  className="mt-4 text-center"
                                  style={{
                                    display: "flex",
                                    justifyContent: "space-evenly",
                                  }}
                                >
                                  {userType === "Admin" ? (
                                    <Button
                                      color="success"
                                      className="w-md waves-effect waves-light"
                                      type="submit"
                                      onClick={adminLogin}
                                    >
                                      Log In
                                    </Button>
                                  ) : null}
                                  {userType === "Author" ? (
                                    <Button
                                      color="primary"
                                      className="w-md waves-effect waves-light"
                                      type="submit"
                                      onClick={() =>
                                        console.log("Author Button")
                                      }
                                    >
                                      Log In
                                    </Button>
                                  ) : null}
                                </div>

                                <div
                                  className="mt-4 text-center"
                                  onClick={() => setStep(3)}
                                  style={{ cursor: "pointer" }}
                                >
                                  <i className="mdi mdi-lock me-1"></i> Forgot
                                  your password?
                                </div>
                              </AvForm>
                            </div>
                          </div>
                        ) : null}
                        {console.log(userType)}
                        {step === 3 ? (
                          <div className="p-2 mt-5">
                            <AvForm className="form-horizontal">
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-mail-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Email</Label>
                                <AvField
                                  name="useremail"
                                  type="email"
                                  validate={{ email: true, required: true }}
                                  className="form-control"
                                  id="useremail"
                                  placeholder="Enter email"
                                />
                              </div>

                              <div className="mt-4 text-center">
                                {userType === "Admin" ? (
                                  <Button
                                    color="success"
                                    className="w-md waves-effect waves-light"
                                    type="submit"
                                    onClick={() => console.log("Admin Button")}
                                  >
                                    Reset
                                  </Button>
                                ) : null}
                                {userType === "Author" ? (
                                  <Button
                                    color="primary"
                                    className="w-md waves-effect waves-light"
                                    type="submit"
                                    onClick={() => {
                                      console.log("Author Button");
                                      setStep(3);
                                    }}
                                  >
                                    Reset
                                  </Button>
                                ) : null}
                              </div>
                            </AvForm>
                          </div>
                        ) : null}
                        {step === 3 ? (
                          <div className="verify-form">
                            <div className="verify-info-container">
                              <p
                                className="verify-info"
                                style={{ textAlign: "center" }}
                              >
                                We have sent an OTP (One Time Password) to you.
                                Please enter the code below to continue
                              </p>
                              {otpSuccess ? (
                                <div>
                                  <UncontrolledAlert
                                    color="success"
                                    className="alert-dismissible fade show"
                                    role="alert"
                                  >
                                    <i className="mdi mdi-check-all me-2"></i>
                                    OTP Verified! You will be redirected to
                                    login!
                                  </UncontrolledAlert>
                                  {setTimeout(() => {
                                    setRedirect(true);
                                  }, 2000)}
                                  {redirect ? <Redirect to="/login" /> : null}
                                </div>
                              ) : null}
                            </div>
                            <div className="divOuter">
                              <div className="divInner">
                                <input
                                  className="resend-code-input"
                                  type="text"
                                  maxLength={6}
                                  onChange={(e) => setCode(e.target.value)}
                                />
                              </div>
                            </div>
                            <p
                              className="resend-code"
                              style={{ textAlign: "center" }}
                            >
                              Resend Code
                            </p>
                            <div className="continue-button">
                              <Button
                                color="primary"
                                className="w-md waves-effect waves-light"
                                type="submit"
                                onClick={sendCode}
                              >
                                Send Code
                              </Button>
                            </div>
                            <p
                              className="cancel-button"
                              onClick={() => {
                                setStep(1);
                              }}
                            >
                              {" "}
                              Cancel{" "}
                            </p>
                          </div>
                        ) : null}
                      </div>
                    </Col>
                  </Row>
                </div>
              </div>
            </Col>
            <Col lg={8}>
              <div className="authentication-bg">
                <div className="bg-overlay"></div>
              </div>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};

export default Login;
