import React, { Component } from "react";
import { AvForm, AvField } from "availity-reactstrap-validation";
import {
  Row,
  Col,
  Card,
  CardBody,
  FormGroup,
  Button,
  Label,
  InputGroup,
} from "reactstrap";
import { Link } from "react-router-dom";
import Flatpickr from "react-flatpickr";

// datatable related plugins
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
  PaginationListStandalone,
  SizePerPageDropdownStandalone,
} from "react-bootstrap-table2-paginator";

import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import "./datatables.scss";
import "./PetEvents.scss";

// Table data
const products = [];

class PetEvents extends Component {
  constructor(props) {
    super(props);
    this.state = {
      breadcrumbItems: [
        { title: "Tables", link: "#" },
        { title: "Data Table", link: "#" },
      ],
      page: 1,
      sizePerPage: 10,
      productData: products,
      addNew: false,
      eventID: "",
      title: "",
      startDate: "",
      endDate: "",
      location: "",
      link: "",
      default_date: new Date(),
      default: false,
      start_date: new Date(),
      monthDate: new Date(),
      yearDate: new Date(),
      end_date: new Date(),
      date: new Date(),
    };

    this.handleStart = this.handleStart.bind(this);
    this.handleEnd = this.handleEnd.bind(this);
  }

  handleDefault(date) {
    this.setState({ default_date: date });
  }
  handleStart = (date) => {
    this.setState({ start_date: date });
  };
  handleEnd = (date) => {
    this.setState({ end_date: date });
  };
  handleMonthChange(date) {
    this.setState({ monthDate: date });
  }
  handleYearChange(date) {
    this.setState({ yearDate: date });
  }

  render() {
    const columns = [
      {
        dataField: "eventID",
        text: "Event ID",
        sort: true,
      },
      {
        dataField: "title",
        text: "Title",
        sort: false,
      },
      {
        dataField: "startDate",
        text: "Start Date",
        sort: true,
      },
      {
        dataField: "endDate",
        text: "End Date",
        sort: true,
      },
      {
        dataField: "location",
        text: "Location",
        sort: true,
      },
      {
        dataField: "link",
        text: "Link",
        sort: true,
      },

      {
        dataField: "menu",
        isDummyField: true,
        text: "Action",
        formatter: () => (
          <>
            <Link to="#" className="me-3 text-primary">
              <a href="/#" onClick={() => {}}>
                <i className="mdi mdi-pencil font-size-18"></i>
              </a>
            </Link>
            <Link to="#" className="text-danger">
              <i className="mdi mdi-trash-can font-size-18"></i>
            </Link>
          </>
        ),
      },
    ];

    const defaultSorted = [
      {
        dataField: "id",
        order: "asc",
      },
    ];

    const pageOptions = {
      sizePerPage: 10,
      totalSize: products.length, // replace later with size(customers),
      custom: true,
    };

    // Select All Button operation
    const selectRow = {
      mode: "checkbox",
    };

    const { SearchBar } = Search;

    return (
      <React.Fragment>
        <div className="page-content">
          {!this.state.addNew ? (
            <div className="container-fluid">
              <Breadcrumbs
                title="Pet Events"
                breadcrumbItems={this.state.breadcrumbItems}
              />

              <Row>
                <Col className="col-12">
                  <Card>
                    <CardBody>
                      <PaginationProvider
                        pagination={paginationFactory(pageOptions)}
                        keyField="id"
                        columns={columns}
                        data={this.state.productData}
                      >
                        {({ paginationProps, paginationTableProps }) => (
                          <ToolkitProvider
                            keyField="id"
                            columns={columns}
                            data={this.state.productData}
                            search
                          >
                            {(toolkitProps) => (
                              <React.Fragment>
                                <Row className="mb-2">
                                  <Col md="4">
                                    <div className="search-box me-2 mb-2 d-inline-block">
                                      <div className="position-relative">
                                        <SearchBar
                                          {...toolkitProps.searchProps}
                                        />
                                        <i className="search-box chat-search-box" />
                                      </div>
                                    </div>
                                  </Col>
                                  <Col sm="8">
                                    <div className="text-sm-end">
                                      <Button
                                        type="button"
                                        color="success"
                                        className="btn-rounded mb-2 me-2"
                                        onClick={() =>
                                          this.setState({ addNew: true })
                                        }
                                      >
                                        <i className="mdi mdi-plus me-1" /> Add
                                        Event
                                      </Button>
                                    </div>
                                  </Col>
                                </Row>

                                <Row>
                                  <Col xl="12">
                                    <div className="table-responsive">
                                      <BootstrapTable
                                        keyField={"id"}
                                        responsive
                                        bordered={false}
                                        striped={false}
                                        defaultSorted={defaultSorted}
                                        selectRow={selectRow}
                                        classes={
                                          "table align-middle table-nowrap"
                                        }
                                        headerWrapperClasses={"thead-light"}
                                        {...toolkitProps.baseProps}
                                        {...paginationTableProps}
                                      />
                                    </div>
                                  </Col>
                                </Row>

                                <Row className="align-items-md-center mt-30">
                                  <Col className="inner-custom-pagination d-flex">
                                    <div className="d-inline">
                                      <SizePerPageDropdownStandalone
                                        {...paginationProps}
                                      />
                                    </div>
                                    <div className="text-md-right ms-auto">
                                      <PaginationListStandalone
                                        {...paginationProps}
                                      />
                                    </div>
                                  </Col>
                                </Row>
                              </React.Fragment>
                            )}
                          </ToolkitProvider>
                        )}
                      </PaginationProvider>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </div>
          ) : (
            <div className="input-add-events">
              <Col lg={6}>
                <Card>
                  <CardBody>
                    <h2>Add an Event</h2>

                    <AvForm>
                      <div className="mb-3">
                        <AvField
                          name="eventID"
                          label="Event ID"
                          placeholder="Add Event ID"
                          type="text"
                          errorMessage="Enter the Event ID"
                          validate={{ required: { value: true } }}
                          onChange={(e) =>
                            this.setState({ eventID: e.target.value })
                          }
                        />
                      </div>
                      <div className="mb-3">
                        <AvField
                          name="title"
                          type="text"
                          label="Title"
                          placeholder="Add Title"
                          errorMessage="Enter the Title"
                          validate={{ required: { value: true } }}
                          onChange={(e) =>
                            this.setState({ title: e.target.value })
                          }
                        />
                      </div>

                      <div className="mb-3">
                        <label>Multiple Date</label>
                        <div className="input-group">
                          <Flatpickr
                            className="form-control d-block"
                            placeholder="dd M,yyyy"
                            options={{
                              mode: "multiple",
                              dateFormat: "Y-m-d",
                            }}
                          />
                        </div>
                      </div>

                      <div className="mb-3">
                        <AvField
                          name="location"
                          label="Location"
                          placeholder="Add Location"
                          type="text"
                          errorMessage="Enter a valid location"
                          validate={{ required: { value: true } }}
                          onChange={(e) =>
                            this.setState({ location: e.target.value })
                          }
                        />
                      </div>
                      <div className="mb-3">
                        <AvField
                          name="url"
                          label="URL"
                          placeholder="Add URL"
                          type="text"
                          errorMessage="Enter a URL"
                          validate={{ required: { value: true } }}
                          onChange={(e) =>
                            this.setState({ link: e.target.value })
                          }
                        />
                      </div>
                      <FormGroup className="mb-0">
                        <div className="add-events-button">
                          <Button
                            type="submit"
                            color="success"
                            className="me-1"
                            onClick={() => {
                              products.push({
                                eventID: this.state.eventID,
                                title: this.state.title,
                                startDate: this.state.startDate,
                                endDate: this.state.endDate,
                                location: this.state.location,
                                link: this.state.link,
                              });
                              this.setState({ addNew: false });
                            }}
                          >
                            Add Event
                          </Button>
                          <Button
                            type="reset"
                            color="secondary"
                            onClick={() => {
                              this.setState({ addNew: false });
                            }}
                          >
                            Cancel
                          </Button>
                        </div>
                      </FormGroup>
                    </AvForm>
                  </CardBody>
                </Card>
              </Col>
            </div>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default PetEvents;
