import React, { useState, Fragment, useContext, useForm } from "react";
import { UncontrolledAlert } from "reactstrap";
import Axios from "axios";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";

import ReadOnly from "./ReadOnly";

import { EventsContext } from "../PetEventsContext";

import "./DataTables.css";

const DataTable = () => {
  //CONTEXT DATA
  const { events, state, columns } = useContext(EventsContext);
  const [eventsValue, setEventsValue] = events;
  const [stateValue, setStateValue] = state;
  const columnsValue = columns;

  //EVENT CREATIION DATA
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");
  const [category, setCategory] = useState("");
  const [file, setFile] = useState(null);
  const [s3File, setS3File] = useState("");

  //SEARCH & EDIT DATA
  const [searchParam, setSearchParam] = useState("");

  const [id, setId] = useState("");
  const [titleE, setTitleE] = useState("");
  const [addressE, setAddressE] = useState("");
  const [startTimeE, setStartTimeE] = useState("");
  const [endTimeE, setEndTimeE] = useState("");
  const [postalCodeE, setPostalCodeE] = useState("");
  const [urlE, setUrlE] = useState("");
  const [detailsE, setDetailsE] = useState("");
  const [categoryE, setCategoryE] = useState("");
  const [editImage, setEditImage] = useState("");

  //FLAGS
  const [createFieldsFilled, setCreateFieldsFilled] = useState(null);
  const [editFieldsFilled, setEditFieldsFilled] = useState(null);
  const [deleteSuccess, setDeleteSuccess] = useState(null);

  //EXPORTED FUNCTIONS
  const deleteEventStatus = (e) => {
    setDeleteSuccess(e);
  };
  const setEditState = () => {
    setStateValue(2);
  };
  const getEditId = (e) => {
    setId(e);
  };
  const getEditTitle = (e) => {
    setTitleE(e);
  };
  const getEditAddress = (e) => {
    setAddressE(e);
  };
  const getStartTime = (e) => {
    setStartTimeE(e);
  };
  const getEndTime = (e) => {
    setEndTimeE(e);
  };
  const getPostalCode = (e) => {
    setPostalCodeE(e);
  };
  const getEditUrl = (e) => {
    setUrlE(e);
  };
  const getEditLocation = (e) => {
    setCategoryE(e);
  };
  const getEditDetails = (e) => {
    setDetailsE(e);
  };
  const getEditImage = (e) => {
    setEditImage(e);
  };

  //CREATE EVENT DATA
  const createEventData = {
    pic: s3File,
    title: title,
    start_time: startTime,
    end_time: endTime,
    postal_code: postalCode,
    location: address,
    url: url,
    event_type: category,
    detail: details,
    createdAt: "2021-11-15T12:15:39.658Z",
  };
  const editEventData = {
    pic: editImage,
    title: titleE,
    start_time: startTimeE,
    end_time: endTimeE,
    postal_code: postalCodeE,
    location: addressE,
    url: urlE,
    event_type: categoryE,
    detail: detailsE,
    createdAt: "2021-11-15T12:15:39.658Z",
  };

  const checkCreateFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      url === "" ||
      file === null ||
      details === ""
    ) {
      setCreateFieldsFilled(false);
    } else {
      setCreateFieldsFilled(true);
    }
  };
  const checkEditFields = () => {
    if (
      titleE === "" ||
      addressE === "" ||
      startTimeE === "" ||
      endTimeE === "" ||
      postalCodeE === "" ||
      categoryE === "" ||
      categoryE === "Select a Category" ||
      urlE === "" ||
      detailsE === ""
    ) {
      setEditFieldsFilled(false);
    } else {
      setEditFieldsFilled(true);
    }
  };
  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..QtsnyXz0fdRJlb_Y-ujbVFWjq1oM72OKw_ygfrSrOAj4pl85x9Ee1T_cKwyM-VfEwrMGwX1iKh5GjEcI-CePaQaKpcfB8pIFvx9Ddzn7U83ZEy3DYydUjC4nwssDP-1jHYHUk9zALR25A9E-hHkHanA0a1_iqzQ1jHipYpM9Ltn3r2h2WQ4ghMbx2UHr1VuHZ91_RaXVb_xOFxogzosNIh8xVySjbUiIubgqkWrC9Fl1gv8lw-uhvRkZdGiLZgtt-lX1rvPENfICa_dSvKhqbRQbKntwxgmaue2kNGG90S_yG9AVxMGsD4c518DRwB3sK7yMtpnVqUxPzmFPGzG2qw";

  const apiUrl = "http://18.216.174.90:3000";
  const createUrl = `${apiUrl}/api/event`;
  const imgUrl = `${apiUrl}/api/user/upload`;
  const editUrl = `${apiUrl}/api/event/${id}`;

  const imgConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "multipart/form-data",
    },
  };

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const fileSelectHandler = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);
    console.log(file);
  };

  const editEvent = (e) => {
    e.preventDefault();
    checkEditFields();
    // if (editFieldsFilled) {
    //   const image = new FormData();
    //   image.append("file", file, file.name);

    //   Axios.post(imgUrl, image, imgConfig)
    //     .then((response) =>
    //       console.log(
    //         setS3File(
    //           response.data.Location.replace(
    //             "https://petappfiles.s3.us-east-2.amazonaws.com/",
    //             ""
    //           )
    //         )
    //       )
    //     )
    //     .catch((err) => console.log("error: " + err));
    // }

    Axios.put(editUrl, editEventData, config)
      .then((response) => {
        window.location.reload(true);
      })
      .catch((err) => console.log("error: " + err));
  };

  const createEvent = (e) => {
    e.preventDefault();
    checkCreateFields();
    if (createFieldsFilled) {
      const image = new FormData();
      if (file !== null) {
        image.append("file", file, file.name);
        console.log(image);
        Axios.post(imgUrl, image, imgConfig)
          .then((response) => {
            console.log(response);
            setS3File(
              response.data.Location.replace(
                "https://petappfiles.s3.us-east-2.amazonaws.com/",
                ""
              )
            );
          })
          .catch((err) => console.log("error: " + err));
      }

      Axios.post(createUrl, createEventData, config)
        .then((response) => {
          window.location.reload(true);
          console.log(response);
        })
        .catch((err) => console.log("error: " + err));
    }
  };

  const styles = {
    borderColor: "red",
  };

  const filteredData = eventsValue.filter((article) =>
    article.title.toLowerCase().includes(searchParam.toLowerCase())
  );

  return (
    <div className="app-container">
      {deleteSuccess === true ? (
        <UncontrolledAlert color="primary">
          Event deleted sucessfully
        </UncontrolledAlert>
      ) : null}
      {deleteSuccess === false ? (
        <UncontrolledAlert color="danger" role="alert">
          Could not delete event
        </UncontrolledAlert>
      ) : null}
      {stateValue === 0 ? (
        <div>
          <div className="table-top">
            <Input
              placeholder="Search the table by title..."
              style={{ width: "25%", borderRadius: "30px", height: "40px" }}
              onChange={(e) => setSearchParam(e.target.value)}
            />
            <Button
              type="button"
              color="success"
              className="btn-rounded mb-2 me-2"
              onClick={() => {
                setStateValue(1);
              }}
