import React, { Component } from "react";
import {
  Form,
  Card,
  CardBody,
  Col,
  Row,
  Button,
  Container,
  Input,
  Label,
} from "reactstrap";

import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

import DataTable from "./DataTable/DataTables";

import { Link } from "react-router-dom";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";

import "./Articles.scss";

// Table data

class Articles extends Component {
  constructor(props) {
    super(props);
    this.state = {
      breadcrumbItems: [
        { title: "Tables", link: "#" },
        { title: "Data Table", link: "#" },
      ],
      page: 1,
      sizePerPage: 10,
      productData: places,
      addNew: false,
      eventID: "",
      title: "",
      startDate: "",
      endDate: "",
      time: "",
      address: "",
      link: "",
      details: "",
      uploadedImage: null,
    };

    this.handleStart = this.handleStart.bind(this);
    this.handleEnd = this.handleEnd.bind(this);
  }

  handleDefault(date) {
    this.setState({ default_date: date });
  }
  handleStart = (date) => {
    this.setState({ start_date: date });
  };
  handleEnd = (date) => {
    this.setState({ end_date: date });
  };
  handleMonthChange(date) {
    this.setState({ monthDate: date });
  }
  handleYearChange(date) {
    this.setState({ yearDate: date });
  }
  handleFileSelect = (event) => {
    this.setState(this.setState({ uploadedImage: event.target.files[0] }));
  };

  render() {
    const columns = [
      {
        dataField: "id",
        text: "ID",
        sort: true,
      },
      {
        dataField: "title",
        text: "Title",
        sort: false,
      },
      {
        dataField: "category",
        text: "Category",
        sort: true,
      },
      {
        dataField: "author",
        text: "Author",
        sort: true,
      },
      {
        dataField: "date",
        text: "Date",
        sort: true,
      },

      {
        dataField: "menu",
        isDummyField: true,
        text: "Action",
        formatter: () => (
          <>
            <Link to="#" className="me-3 text-primary">
              <a
                href="/#"
                onClick={() => {
                  console.log(this.state.eventID);
                }}
              >
                <i className="mdi mdi-pencil font-size-18"></i>
              </a>
            </Link>
            <Link to="#" className="text-danger">
              <i className="mdi mdi-trash-can font-size-18"></i>
            </Link>
          </>
        ),
      },
    ];

    const defaultSorted = [
      {
        dataField: "id",
        order: "asc",
      },
    ];

    const pageOptions = {
      sizePerPage: 10,
      totalSize: places.length, // replace later with size(customers),
      custom: true,
    };

    // Select All Button operation
    const selectRow = {
      mode: "checkbox",
    };

    const { SearchBar } = Search;

    return (
      <React.Fragment>
        <div className="page-content">
          <div className="container-fluid">
            <Breadcrumbs title="Articles" />
            <DataTable />
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Articles;
