import React, { useState, createContext, useEffect } from "react";
import Axios from "axios";

export const EventsContext = createContext();

export const PetEventsProvider = (props) => {
  const [eventsData, setEventsData] = useState([]);
  const [token, setToken] = useState("");
  const apiUrl = "http://18.216.174.90:3000";
  const getEventsUrl = `${apiUrl}/api/event`;
  const tokenValue = String(localStorage.getItem("jwtToken"));
  setToken(tokenValue);
  const loginInfo = {
    name: "m.naveedashfaq@gmail.com",
    password: "123456",
  };

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  useEffect(() => {
    Axios.get(getEventsUrl, config).then((response) =>
      setEventsData(response.data)
    );
  }, []);

  const [state, setState] = useState(0);

  const columns = [
    {
      title: "Title",
    },
    {
      title: "Address",
    },
    {
      title: "Category",
    },
    {
      title: "Start Time",
    },
    {
      title: "End Time",
    },
    {
      title: "Postal Code",
    },
    {
      title: "URL",
    },

    {
      title: "Action",
    },
  ];

  return (
    <EventsContext.Provider
      value={{
        events: [eventsData, setEventsData],
        state: [state, setState],
        columns: columns,
        jwtToken: token,
      }}
    >
      {props.children}
    </EventsContext.Provider>
  );
};
