import Axios from "axios";
import IconButton from "@mui/material/IconButton";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import { Link } from "react-router-dom";
import { styled } from "@mui/material/styles";

import Button from "@mui/material/Button";
import AddIcon from "@mui/icons-material/Add";
import TextField from "@mui/material/TextField";
import { AuthContext } from "../../AuthContext";
import Box from "@mui/material/Box";
import InputAdornment from "@mui/material/InputAdornment";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

const columns = [
  { id: "title", label: "Title", minWidth: 100 },
  { id: "author", label: "Author", minWidth: 100 },
  { id: "long", label: "Likes", minWidth: 100 },
  { id: "lat", label: "Views", minWidth: 100 },
  { id: "open", label: "Comments", minWidth: 100 },
  { id: "close", label: "Featured", minWidth: 100 },
  { id: "close", label: "Actions", minWidth: 100 },
];

export default function StickyHeadTable({ token, apiUrl }) {
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(50);
  const [petArticles, setArticles] = useState([]);
  const [search, setSearch] = useState("");

  const imgConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "multipart/form-data",
    },
  };

  const imgUrl = `http://3.15.82.201:3000/api/v1/user/upload`;
  const editApi = `${apiUrl}/petplace`;

  const Input = styled("input")({
    display: "none",
  });

  const buttonStyle = {
    backgroundColor: "#1ca395",
    marginLeft: "10px",
  };
  const searchStyle = {
    width: "350px",
  };

  // EDIT DATA
  const [table, setTable] = useState(true);
  const [id, setId] = useState("");
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [longitude, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [category, setCategory] = useState("");
  const [phone, setPhone] = useState("");
  const [fb, setFb] = useState("");
  const [insta, setInsta] = useState("");
  const [url, setUrl] = useState("");
  const [email, setEmail] = useState("");
  const [file, setFile] = useState(null);
  const [s3File, setS3File] = useState("");

  const fileSelectHandler = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);
    const image = new FormData();
    if (file !== null) {
      image.append("file", file, file.name);
      console.log(image);
      Axios.post(imgUrl, image, imgConfig)
        .then((response) => {
          setS3File(response.data.key);
          console.log(s3File);
        })
        .catch((err) => console.log("error: " + err));
    }
  };

  const bodyParameters = {
    pic: {
      pic: [
        {
          url: s3File,
        },
      ],
    },
    title: title,
    title_icon: "760634d2-f8ec-4caa-9a2d-14a8828cfb5dpetplace.jpg",
    social_link: {
      social_link: [
        {
          facebook: fb,
        },
        {
          instagram: insta,
        },
      ],
    },
    phone_number: phone,
    address: address,
    lat: latitude,
    long: longitude,
    postcode: postalCode,
    start_time: startTime,
    end_time: endTime,
    email: email,
    web_url: url,
    createdAt: "2021-11-15T12:15:39.658Z",
    category: 1,
  };

  const articleApi = `${apiUrl}/article`;

  const editPlace = (e) => {
    e.preventDefault();

    Axios.put(`${articleApi}/${id}`, bodyParameters, config)
      .then((response) => {
        window.location.reload(true);
      })
      .catch((err) => console.log("error: " + err));
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  useEffect(() => {
    Axios.get(articleApi, config).then((response) => {
      setArticles(response.data.data);
    });
  }, []);

  const filteredArticles = petArticles.filter((place) =>
    place.header.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Fragment>
      {table ? (
        <Fragment>
          <h1 style={{ color: "#343a40" }}> Articles </h1>
          <div className="places-actions">
            <TextField
              label="Search the articles..."
              sx={searchStyle}
              onChange={(e) => setSearch(e.target.value)}
            />
            <Link to="/places/add-place" style={{ textDecoration: "none" }}>
              <Button
                sx={buttonStyle}
                variant="contained"
                startIcon={<AddIcon />}
              >
                Add Place
              </Button>
            </Link>
          </div>
          <Paper sx={{ width: "100%", height: "100%", overflow: "hidden" }}>
            <TableContainer sx={{ maxHeight: 440 }}>
              <Table stickyHeader aria-label="sticky table">
                <TableHead>
                  <TableRow>
                    {columns.map((column) => (
                      <TableCell
                        key={column.id}
                        align={column.align}
                        style={{ minWidth: column.minWidth }}
                      >
                        {column.label}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {filteredArticles
                    .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                    .map((article, idx) => {
                      return (
                        <TableRow
                          hover
                          role="checkbox"
                          tabIndex={-1}
                          key={article.id}
                        >
                          <TableCell>{article.header}</TableCell>
                          <TableCell>
                            {article.author.firstName + article.author.lastName}
                          </TableCell>
                          <TableCell>{article.likes_count}</TableCell>
                          <TableCell>{article.views_count}</TableCell>
                          <TableCell>{article.comment.length}</TableCell>
                          <TableCell>
                            {article.isFeatured ? "Featured" : "Not Featured"}
                          </TableCell>
                          <TableCell>
                            <div>
                              <IconButton
                                aria-label="delete"
                                fontSize="small"
                                color="error"
                                onClick={(e) => {
                                  e.preventDefault();
                                  Axios.delete(`${articleApi}/${article.id}`, {
                                    headers: {
                                      Authorization: `Bearer ${token}`,
                                    },
                                  })
                                    .then((res) => {
                                      console.log(res);
                                      window.location.reload(true);
                                    })
                                    .catch((err) => console.log(err));
                                }}
                              >
                                <DeleteIcon />
                              </IconButton>

                              <IconButton
                                aria-label="delete"
                                fontSize="small"
                                color="primary"
                                onClick={() => {
                                  setId(article.id);
                                  setTitle(article.title);
                                  setAddress(article.address);
                                  setLongitude(article.long);
                                  setLatitude(article.lat);
                                  setStartTime(article.start_time);
                                  setEndTime(article.end_time);
                                  setPostalCode(article.postcode);
                                  setCategory(article.category.name);
                                  setPhone(article.phone_number);
                                  setFb(
                                    article.social_link.social_link[0].facebook
                                  );
                                  setInsta(
                                    article.social_link.social_link[1].instagram
                                  );
                                  setUrl(article.web_url);
                                  setEmail(article.email);
                                  setTable(false);
                                }}
                              >
                                <EditIcon />
                              </IconButton>
                            </div>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[50, 100, 150]}
              component="div"
              count={filteredArticles.length}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
            />
          </Paper>
        </Fragment>
      ) : (
        <div className="editplace-container">
          <h1 style={{ color: "#343a40" }}> Edit the place </h1>
          <div style={{ display: "flex" }}>
            <Box
              component="form"
              sx={{
                "& > :not(style)": { m: 1, width: "60ch" },
                display: "flex",
                flexDirection: "column",
              }}
              noValidate
              autoComplete="off"
            >
              <TextField
                id="outlined-basic"
                label="Title"
                value={title}
                variant="outlined"
                onChange={(e) => setTitle(e.target.value)}
              />
              <TextField
                id="outlined-basic"
                label="Address"
                value={address}
                variant="outlined"
                onChange={(e) => setAddress(e.target.value)}
              />
              <TextField
                id="outlined-basic"
                value={longitude}
                label="Longitude"
                variant="outlined"
                onChange={(e) => setLongitude(e.target.value)}
              />
              <TextField
                id="outlined-basic"
                value={latitude}
                label="Latitude"
                variant="outlined"
                onChange={(e) => setLatitude(e.target.value)}
              />
              <TextField
                id="outlined-basic"
                label="Insta Link"
                value={insta}
                variant="outlined"
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      https://www.
                    </InputAdornment>
                  ),
                }}
                onChange={(e) => setInsta(e.target.value)}
              />
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <input
                  type="datetime-local"
                  className="datepicker"
                  defaultValue={startTime}
                  onChange={(e) => setStartTime(e.target.value)}
                />
                <input
                  type="datetime-local"
                  defaultValue={endTime}
                  className="datepicker"
                  onChange={(e) => setEndTime(e.target.value)}
                />
              </div>
              <label htmlFor="contained-button-file">
                <Input
                  accept="image/*"
                  id="contained-button-file"
                  multiple
                  type="file"
                  onChange={fileSelectHandler}
                />
                <Button variant="contained" component="span">
                  Upload an image
                </Button>
              </label>
            </Box>
            <Box
              component="form"
              sx={{
                "& > :not(style)": { m: 1, width: "60ch" },
                display: "flex",
                flexDirection: "column",
              }}
              noValidate
              autoComplete="off"
            >
              <TextField
                id="outlined-basic"
                label="Postal Code"
                value={postalCode}
                variant="outlined"
                onChange={(e) => setPostalCode(e.target.value)}
              />
              <FormControl sx={{ m: 1, minWidth: 120 }}>
                <InputLabel id="demo-simple-select-helper-label">
                  Category
                </InputLabel>
                <Select
                  labelId="demo-simple-select-helper-label"
                  id="demo-simple-select-helper"
                  label="Age"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                >
                  <MenuItem value="Restaurant">Restaurant</MenuItem>
                  <MenuItem value="Cafe">Cafe</MenuItem>
                  <MenuItem value="Pub">Pub</MenuItem>
                  <MenuItem value="Park">Park</MenuItem>
                  <MenuItem value="Hotel">Hotel</MenuItem>
                </Select>
              </FormControl>
              <TextField
                id="outlined-basic"
                label="Phone"
                variant="outlined"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
              <TextField
                id="outlined-basic"
                label="Email"
                variant="outlined"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <TextField
                id="outlined-start-adornment"
                sx={{ m: 1, width: "25ch" }}
                value={url}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      https://www.
                    </InputAdornment>
                  ),
                }}
                onChange={(e) => setUrl(e.target.value)}
              />
              <TextField
                id="outlined-basic"
                label="Facebook Link"
                variant="outlined"
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      https://www.
                    </InputAdornment>
                  ),
                }}
                value={fb}
                onChange={(e) => setFb(e.target.value)}
              />
            </Box>
          </div>
          <div
            style={{ display: "flex", marginTop: "20px", marginLeft: "10px" }}
          >
            <Button
              variant="contained"
              color="error"
              onClick={() => setTable(true)}
            >
              Cancel
            </Button>
            <Button style={buttonStyle} variant="contained">
              Confirm Changes
            </Button>
          </div>
        </div>
      )}
    </Fragment>
  );
}
