import React, { useState, Fragment, useContext } from "react";
import { UncontrolledAlert } from "reactstrap";
import Axios from "axios";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";

import ReadOnly from "./ReadOnly";

import { EventsContext } from "../PetEventsContext";

import "./DataTables.css";

const DataTable = () => {
  //CONTEXT DATA
  const { events, state, columns, token } = useContext(EventsContext);
  const [eventsValue, setEventsValue] = events;
  const [stateValue, setStateValue] = state;
  const columnsValue = columns;
  const tokenValue = token;
  console.log(tokenValue);

  //EVENT CREATIION DATA
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");
  const [category, setCategory] = useState("");
  const [file, setFile] = useState(null);
  const [s3File, setS3File] = useState("");

  //SEARCH & EDIT DATA
  const [searchParam, setSearchParam] = useState("");
  const [editEventData, setEditEventData] = useState([
    {
      id: "",
      title: "",
      location: "",
      event: "",
      start: "",
      end: "",
      post: "",
      url: "",
      details: "",
    },
  ]);

  //FLAGS
  const [fieldsFilled, setFieldsFilled] = useState(null);
  const [deleteSuccess, setDeleteSuccess] = useState(null);

  //EXPORTED FUNCTIONS
  const deleteEventStatus = (e) => {
    setDeleteSuccess(e);
  };
  const getEditData = (e) => {
    setEditEventData(e);
  };
  const setEditState = () => {
    setStateValue(2);
  };

  //CREATE EVENT DATA
  const createEventData = {
    pic: s3File,
    title: title,
    start_time: "2021-11-15T12:15:39.658Z",
    end_time: "2021-11-15T12:15:39.658Z",
    postal_code: postalCode,
    location: address,
    url: url,
    event_type: category,
    detail: details,
    createdAt: "2021-11-15T12:15:39.658Z",
  };

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      url === "" ||
      file === null ||
      details === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };
  // const token =
  //   "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..h92VwIoPuOTy9Up_jo08uTe4k6FQF81pT_ktyzFlcWNdlNj3iUwfox2HrKY6UxDKbzmijIsYF5ARwC_43NxZT1bIWHsAL7fNPG-XaNfADvvmy9m8aVgTj4NslKRWfhBBrkygjbUcuhumE1SOHMF78c4cDk28hiH8096WqCBZoPufqfcOW9HM6k6WtXRsGHasaYa2VUz9k05BWpoIsfB_UvrVcPPIMyWko_YqKfJ1inKfZQ-TEiSH5STf9vgQB_9y54rh3FbinPmiBW-g6_IoLSiNFrjZmIdblnzuiEANihZb-TtLFzmzXctW6YmFftvMcoPFhqqqTMwpTAGixacnuA";

  const apiUrl = "http://18.216.174.90:3000";
  const createUrl = `${apiUrl}/api/event`;
  const imgUrl = `${apiUrl}/api/user/upload`;
  const editUrl = `${apiUrl}/api/event/${editEventData[0].id}`;

  const imgConfig = {
    headers: {
      Authorization: `Bearer ${tokenValue}`,
      "Content-Type": "multipart/form-data",
    },
  };

  const config = {
    headers: {
      Authorization: `Bearer ${tokenValue}`,
    },
  };

  const fileSelectHandler = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);
  };

  const editEvent = (e) => {
    e.preventDefault();
    checkFields();
    const image = new FormData();
    image.append("file", file, file.name);

    Axios.post(imgUrl, image, imgConfig)
      .then((response) =>
        console.log(
          setS3File(
            response.data.Location.replace(
              "https://petappfiles.s3.us-east-2.amazonaws.com/",
              ""
            )
          )
        )
      )
      .catch((err) => console.log("error: " + err));
    if (fieldsFilled) {
      Axios.put(editUrl, createEventData, config)
        .then((response) => {
          window.location.reload(true);
        })
        .catch((err) => console.log("error: " + err));
    }
  };

  const createEvent = (e) => {
    e.preventDefault();
    checkFields();
    if (fieldsFilled) {
      const image = new FormData();
      image.append("file", file, file.name);

      Axios.post(imgUrl, image, imgConfig)
        .then((response) =>
          console.log(
            setS3File(
              response.data.Location.replace(
                "https://petappfiles.s3.us-east-2.amazonaws.com/",
                ""
              )
            )
          )
        )
        .catch((err) => console.log("error: " + err));

      Axios.post(createUrl, createEventData, config)
        .then((response) => {
          window.location.reload(true);
        })
        .catch((err) => console.log("error: " + err));
    }
  };

  const styles = {
    borderColor: "red",
  };

  const filteredData = eventsValue.filter((article) =>
    article.title.toLowerCase().includes(searchParam.toLowerCase())
  );

  return (
    <div className="app-container">
      {deleteSuccess === true ? (
        <UncontrolledAlert color="primary">
          Event deleted sucessfully
        </UncontrolledAlert>
      ) : null}
      {deleteSuccess === false ? (
        <UncontrolledAlert color="danger" role="alert">
          Could not delete event
        </UncontrolledAlert>
      ) : null}
      {stateValue === 0 ? (
        <div>
          <div className="table-top">
            <Input
              placeholder="Search the table by title..."
              style={{ width: "25%", borderRadius: "30px", height: "40px" }}
              onChange={(e) => setSearchParam(e.target.value)}
            />
            <Button
              type="button"
              color="success"
              className="btn-rounded mb-2 me-2"
              onClick={() => {
                setStateValue(1);
              }}
            >
              <i className="mdi mdi-plus me-1" /> Add Event
            </Button>
          </div>
          <div style={{ width: "100%", overflow: "auto" }}>
            <table style={{ fontSize: "5px" }}>
              <thead>
                {columnsValue.map((header) => (
                  <th> {header.title} </th>
                ))}
              </thead>
              <tbody>
                {filteredData.map((data) => (
                  <Fragment>
                    <ReadOnly
                      data={data}
                      id={data.id}
                      deleteEventStatus={deleteEventStatus}
                      getEditData={getEditData}
                      editState={setEditState}
                      token={token}
                    />
                  </Fragment>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : null}
      {stateValue === 1 ? (
        <div className="input-add-events">
          <Container fluid>
            <Row>
              <Col xs={12}>
                <Card>
                  <CardBody>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-text-input"
                        className="col-md-2 col-form-label"
                      >
                        Title*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            title === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Title"
                          }
                          id="title"
                          onChange={(e) => setTitle(e.target.value)}
                          style={
                            title === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Upload Images*
                      </Label>
                      <Col md={10}>
                        <div className="input-group">
                          <input
                            type="file"
                            className="form-control"
                            id="customFile"
                            onChange={fileSelectHandler}
                            style={
                              file === null && fieldsFilled === false
                                ? styles
                                : null
                            }
                          />
                        </div>
                      </Col>
                    </Row>

                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Address*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            address === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Address"
                          }
                          id="title"
                          onChange={(e) => setAddress(e.target.value)}
                          style={
                            address === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label className="col-md-2 col-form-label">
                        Category*
                      </Label>
                      <Col md={10}>
                        <select
                          className="form-control"
                          onChange={(e) => setCategory(e.target.value)}
                          style={
                            (category === "" ||
                              category === "Select a Category") &&
                            fieldsFilled === false
                              ? styles
                              : null
                          }
                        >
                          <option defaultValue>Select a Category</option>
                          <option value="Resturant">Online</option>
                          <option value="Cafe">Offline</option>
                        </select>
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-time-input"
                        className="col-md-2 col-form-label"
                      >
                        Start Time*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="datetime-local"
                          placeholder={
                            startTime === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Start Time"
                          }
                          id="example-time-input"
                          onChange={(e) => setStartTime(e.target.value)}
                          style={
                            startTime === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-time-input"
                        className="col-md-2 col-form-label"
                      >
                        End Time*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="datetime-local"
                          placeholder={
                            endTime === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter End Time"
                          }
                          id="example-time-input"
