import React, { Component } from "react";
import { Row, Col, Button, Container, Label } from "reactstrap";

import { CognitoUserPool } from "amazon-cognito-identity-js";
// availity-reactstrap-validation
import { AvForm, AvField } from "availity-reactstrap-validation";
// action

import {
  registerUser,
  registerUserFailed,
  apiError,
} from "../../store/actions";

// Redux
import { connect } from "react-redux";
import { Link } from "react-router-dom";

// import images
import logodark from "../../assets/images/logo-dark.png";

class RegisterAdmin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      fullName: "",
      password: "",
      phone: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.signup = this.signup.bind(this);
  }
  poolData = {
    UserPoolId: "ap-south-1_wsc5XApxg",
    ClientId: "64bqub17ktjan94145esiu42vn",
  };

  UserPool = new CognitoUserPool(this.poolData);

  handleSubmit(event, values) {
    this.props.registerUser(values);
  }

  componentDidMount() {
    this.props.registerUserFailed("");
    this.props.apiError("");
    document.body.classList.add("auth-body-bg");
  }

  render() {
    return (
      <React.Fragment>
        <div>
          <Container fluid className="p-0">
            <Row className="g-0">
              <Col lg={4}>
                <div className="authentication-page-content p-4 d-flex align-items-center min-vh-100">
                  <div className="w-100">
                    <Row className="justify-content-center">
                      <Col lg={9}>
                        <div>
                          <div className="text-center">
                            <div>
                              <Link to="#" className="logo">
                                <img src={logodark} height="20" alt="logo" />
                              </Link>
                            </div>

                            <h4 className="font-size-18 mt-4">
                              Register Your Admin Account
                            </h4>
                          </div>

                          {/* {this.props.user && (
                            <Alert color="success">
                              Registration Done Successfully.
                            </Alert>
                          )}

                          {this.props.registrationError && (
                            <Alert color="danger">
                              {this.props.registrationError}
                            </Alert>
                          )} */}

                          <div className="p-2 mt-5">
                            <AvForm
                              onValidSubmit={this.handleSubmit}
                              className="form-horizontal"
                            >
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-user-3-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Full Name</Label>
                                <AvField
                                  name="firstname"
                                  validate={{ required: true }}
                                  type="text"
                                  className="form-control"
                                  id="firstname"
                                  placeholder="Enter your full name"
                                  onChange={(e) => {
                                    this.setState({ fullName: e.target.value });
                                  }}
                                />
                              </div>

                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-mail-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Email</Label>
                                <AvField
                                  name="email"
                                  validate={{ email: true, required: true }}
                                  type="email"
                                  className="form-control"
                                  id="useremail"
                                  placeholder="Enter your email"
                                  onChange={(e) => {
                                    this.setState({ email: e.target.value });
                                  }}
                                />
                              </div>
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-phone-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Mobile</Label>
                                <AvField
                                  name="number"
                                  placeholder="Enter Only number"
                                  type="number"
                                  errorMessage="Enter Only Number"
                                  validate={{
                                    required: { value: true },
                                    pattern: {
                                      value: "^[0-9]+$",
                                      errorMessage: "Only Numbers",
                                    },
                                  }}
                                  onChange={(e) => {
                                    this.setState({ phone: e.target.value });
                                  }}
                                />
                              </div>
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-lock-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Password</Label>
                                <AvField
                                  name="pass"
                                  type="password"
                                  className="form-control"
                                  id="userpassword"
                                  placeholder="Enter password"
                                  onChange={(e) => {
                                    this.setState({ password: e.target.value });
                                  }}
                                />
                              </div>
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-lock-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">
                                  Confirm Password
                                </Label>
                                <AvField
                                  name="pass"
                                  type="password"
                                  className="form-control"
                                  id="userpassword"
                                  placeholder="Enter password"
                                />
                              </div>

                              <div className="text-center">
                                <Button
                                  color="primary"
                                  className="w-md waves-effect waves-light"
                                  type="submit"
                                  onClick={this.signup}
                                >
                                  {this.props.loading
                                    ? "Loading ..."
                                    : "Register"}
                                </Button>
                              </div>

                              <div className="mt-4 text-center">
                                <p className="mb-0">
                                  By registering you agree to the Nazox
                                  <Link to="#" className="text-primary">
                                    {" "}
                                    Terms of Use
                                  </Link>
                                </p>
                                <p>
                                  Already have an account?{" "}
                                  <Link
                                    to="/login"
                                    className="fw-medium text-primary"
                                  >
                                    Login
                                  </Link>
                                </p>
                              </div>
                            </AvForm>
                          </div>
                        </div>
                      </Col>
                    </Row>
                  </div>
                </div>
              </Col>
              <Col lg={8}>
                <div className="authentication-bg">
                  <div className="bg-overlay"></div>
                </div>
              </Col>
            </Row>
          </Container>
        </div>
      </React.Fragment>
    );
  }
}

const mapStatetoProps = (state) => {
  const { user, registrationError, loading } = state.Account;
  return { user, registrationError, loading };
};

export default connect(mapStatetoProps, {
  registerUser,
  apiError,
  registerUserFailed,
})(RegisterAdmin);
