import React, { useState, createContext, useEffect, useContext } from "react";
import Axios from "axios";
import { AuthContext } from "../../AuthContext";

export const ArticlesContext = createContext();

export const ArticlesProvider = (props) => {
  const [articles, setArticles] = useState([]);
  const [state, setState] = useState(0);
  const apiUrl = "http://18.216.174.90:3000/api/article";
  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..k3o6yQwCrctHrzjhbEr70u02gAsUNVnwp3Cwe97ddkfOzWMGtcc8pK3F79CplWsrwrNb4-NPfMsW49zdpaFIbv7Ir2Zf_jfLlje4vjlOomUwNXGaauL4q2fmHc7L3CRn1VdVUV1IBFEbV2vUoJYvpit79Yqw3Pcqlds3u-OyguNzpew-Bbit-CNrHQwfTL2mQ1XH1Spbe9w1ZhByELyMvhAzWMWXymnxtVGembwSah0ARYjVuGi_Ig5N9xjE8mbM532CWgLUHXzEsQtLTgnk9MHm86-sEzcizUQ7gtu8SgZFqLJbHA9Bw_ocaMiPT1CDTdhgRLwetJPriVrX8YgbCw";

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  useEffect(() => {
    Axios.get(apiUrl, config).then((response) => setArticles(response.data));
  }, []);

  const columns = [
    {
      title: "Title",
    },
    {
      title: "Author",
    },
    {
      title: "Likes",
    },
    {
      title: "Views",
    },
    {
      title: "Comments",
    },
    {
      title: "Featured",
    },
    {
      title: "Action",
    },
  ];

  return (
    <ArticlesContext.Provider
      value={{
        articles: [articles, setArticles],
        state: [state, setState],
        columns: columns,
      }}
    >
      {props.children}
    </ArticlesContext.Provider>
  );
};
