import React, { useState, Fragment, useContext } from "react";

import { Button, Input } from "reactstrap";

import ReadOnly from "./ReadOnly";
import EditableData from "./EditableData";

import { PlacesContext } from "../PetPlaces/PlacesContext";

import "./DataTables.css";

const DataTable = () => {
  const [places, setPlaces] = useContext(PlacesContext);
  const [addNew, setAddNew] = useContext(PlacesContext);

  const [addFormData, setFormData] = useState({});
  const headers = [
    {
      title: "Name",
    },
    {
      title: "Position",
    },
    {
      title: "Office",
    },
    {
      title: "Age",
    },
    {
      title: "Start Date",
    },
    {
      title: "Salary",
    },
    {
      title: "Actions",
    },
  ];

  const [searchParam, setSearchParam] = useState("");
  const [editContactId, setEditContactId] = useState(null);

  const handleEditClick = (event, product) => {
    event.preventDefault();
    setEditContactId(product.id);
  };

  const filteredData = places.filter((place) =>
    place.name.toLowerCase().includes(searchParam.toLowerCase())
  );
  console.log(addNew);

  return (
    <div className="app-container">
      <div className="table-top">
        <Input
          placeholder="Search the table by name..."
          style={{ width: "25%", borderRadius: "30px", height: "40px" }}
          onChange={(e) => setSearchParam(e.target.value)}
        />
        <Button
          type="button"
          color="success"
          className="btn-rounded mb-2 me-2"
          onClick={() => {
            setAddNew(true);
            console.log(addNew);
          }}
        >
          <i className="mdi mdi-plus me-1" /> Add Place
        </Button>
      </div>
      <table>
        <thead>
          {headers.map((header) => (
            <th> {header.title} </th>
          ))}
        </thead>
        <tbody>
          {filteredData.map((product) => (
            <Fragment>
              {editContactId === product.id ? (
                <EditableData data={product} />
              ) : (
                <ReadOnly data={product} handleEditClick={handleEditClick} />
              )}
            </Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataTable;

