import React, { useState, Fragment, useContext } from "react";

import Axios from "axios";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";

import ReadOnly from "./ReadOnly";
import EditableData from "./EditableData";

import { EventsContext } from "../PetEventsContext";

import "./DataTables.css";

const DataTable = () => {
  const { places, addNew, columns } = useContext(EventsContext);
  const [placesValue, setPlacesValue] = places;
  const [addNewValue, setAddNewValue] = addNew;
  const [columnsValue, setcolumnsvalue] = columns;

  //USR DATA

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");

  const [titleAdded, setTitleAdded] = useState(null);
  const [addressAdded, setAddressAdded] = useState(null);
  const [longituideAdded, setLongitudeAdded] = useState(null);
  const [latitudeAdded, setLatitudeAdded] = useState(null);
  const [startTimeAdded, setStartTimeAdded] = useState(null);
  const [endTimeAdded, setEndTimeAdded] = useState(null);
  const [postalCodeAdded, setPostalCodeAdded] = useState(null);
  const [categoryAdded, setCategoryAdded] = useState(null);
  const [phoneAdded, setPhoneAdded] = useState(null);
  const [fbAdded, setFbAdded] = useState(null);
  const [instaAdded, setInstaAdded] = useState(null);
  const [twitterAdded, setTwitterAdded] = useState(null);
  const [urlAdded, setUrlAdded] = useState(null);
  const [emailAdded, setEmailAdded] = useState(null);

  const [searchParam, setSearchParam] = useState("");
  const [editContactId, setEditContactId] = useState(null);

  const [fieldsFilled, setFieldsFilled] = useState(null);

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      startDate === "" ||
      endDate === "" ||
      url === "" ||
      details === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };

  const styles = {
    borderColor: "red",
  };

  const handleEditClick = (event, product) => {
    event.preventDefault();
    setEditContactId(product.id);
  };

  const filteredData = placesValue.filter((place) =>
    place.name.toLowerCase().includes(searchParam.toLowerCase())
  );

  const addPlace = (event) => {
    checkFields();
    if (fieldsFilled === true) {
      console.log("FILLED");
    } else {
      console.log("EMPTY");
    }
  };

  return (
    <div className="app-container">
      {!addNewValue ? (
        <div>
          <div className="table-top">
            <Input
              placeholder="Search the table by name..."
              style={{ width: "25%", borderRadius: "30px", height: "40px" }}
              onChange={(e) => setSearchParam(e.target.value)}
            />
            <Button
              type="button"
              color="success"
              className="btn-rounded mb-2 me-2"
              onClick={() => {
                setAddNewValue(true);
              }}
            >
              <i className="mdi mdi-plus me-1" /> Add Event
            </Button>
          </div>
          <div style={{ width: "100%", overflow: "auto" }}>
