import React, { useState } from "react";
import { Link } from "react-router-dom";

const EditableData = ({ data, id }) => {
  const styles = {
    backgroundColor: "#EEEEEE",
    border: "none",
    height: "30px",
  };
  const errStyles = {
    borderColor: "red",
  };
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [longituide, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [category, setCategory] = useState("");
  const [phone, setPhone] = useState("");
  const [fb, setFb] = useState("");
  const [insta, setInsta] = useState("");
  const [twitter, setTwitter] = useState("");
  const [url, setUrl] = useState("");
  const [email, setEmail] = useState("");
  const [file, setFile] = useState([]);
  const [s3File, setS3File] = useState("");

  const [fieldsFilled, setFieldsFilled] = useState(null);

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      longituide === "" ||
      latitude === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      phone === "" ||
      fb === "" ||
      insta === "" ||
      twitter === "" ||
      url === "" ||
      email === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };

  return (
    <tr>
      <td>
        <input
          required
          placeholder="Enter the title..."
          type="text"
          style={styles}
          onClick={(e) => setTitle(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the address..."
          type="text"
          style={styles}
          onClick={(e) => setAddress(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the longitude..."
          type="text"
          style={styles}
          onClick={(e) => setLongitude(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the latitude..."
          type="text"
          style={styles}
          onClick={(e) => setLatitude(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the open time..."
          type="time"
          style={styles}
          onClick={(e) => setStartTime(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the end time..."
          type="time"
          style={styles}
          onClick={(e) => setEndTime(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the postal code..."
          type="text"
          style={styles}
          onClick={(e) => setPostalCode(e.target.value)}
        />
      </td>
      <td>
        <select
          className="form-control"
          onChange={(e) => setCategory(e.target.value)}
          style={
            (category === "" || category === "Select a Category") &&
            fieldsFilled === false
              ? styles
              : null
          }
        >
          <option defaultValue>Select a Category</option>
          <option value="Resturant">Resturant</option>
          <option value="Cafe">Cafe</option>
          <option value="Pub">Pub</option>
          <option value="Park">Park</option>
          <option value="Hotel">Hotel</option>
        </select>
      </td>
      <td>
        <input
          required
          placeholder="Enter the phone..."
          type="text"
          style={styles}
          onClick={(e) => setPhone(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the FB link..."
          type="text"
          style={styles}
          onClick={(e) => setFb(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the Insta link..."
          type="text"
          style={styles}
          onClick={(e) => setInsta(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the Twitter link..."
          type="text"
          style={styles}
          onClick={(e) => setTwitter(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the Email..."
          type="text"
          style={styles}
          onClick={(e) => setEmail(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the Email..."
          type="text"
          style={styles}
          onClick={(e) => setEmail(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the URL..."
          type="text"
          style={styles}
          onClick={(e) => setUrl(e.target.value)}
        />
      </td>
      <td>
        <Link to="#" className="me-3 text-primary">
          <i className="mdi mdi-floppy font-size-18"></i>
        </Link>
        <Link to="#" className="text-danger">
          <i className="mdi mdi-trash-can font-size-18"></i>
        </Link>
      </td>
    </tr>
  );
};

export default EditableData;
