import React, { Component } from "react";
import {
  Form,
  Card,
  CardBody,
  Col,
  Row,
  Button,
  Input,
  Container,
  Label,
  Modal,
  ModalHeader,
  ModalBody,
} from "reactstrap";
import emailjs from "emailjs-com";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import cellEditFactory from "react-bootstrap-table2-editor";
import { init } from "emailjs-com";

import { Link } from "react-router-dom";
import adminusers from "./adminuser.json";
// datatable related plugins
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
  PaginationListStandalone,
  SizePerPageDropdownStandalone,
} from "react-bootstrap-table2-paginator";

import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import "./datatables.scss";
import "./AdminUser.scss";

// Table data

class AdminUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      breadcrumbItems: [
        { title: "Tables", link: "#" },
        { title: "Data Table", link: "#" },
      ],
      page: 1,
      sizePerPage: 10,
      productData: adminusers,
      addNew: false,
      eventID: "",
      title: "",
      startDate: "",
      endDate: "",
      time: "",
      address: "",
      link: "",
      details: "",
      uploadedImage: null,
    };

    this.handleStart = this.handleStart.bind(this);
    this.handleEnd = this.handleEnd.bind(this);
  }

  handleDefault(date) {
    this.setState({ default_date: date });
  }
  handleStart = (date) => {
    this.setState({ start_date: date });
  };
  handleEnd = (date) => {
    this.setState({ end_date: date });
  };

  render() {
    const columns = [
      {
        dataField: "id",
        text: "ID",
        sort: true,
      },
      {
        dataField: "name",
        text: "Name",
        sort: false,
      },
      {
        dataField: "email",
        text: "Email",
        sort: true,
      },
      {
        dataField: "phone",
        text: "Phone",
        sort: true,
      },

      {
        dataField: "menu",
        isDummyField: true,
        text: "Action",
        formatter: () => (
          <>
            <Link to="#" className="me-3 text-primary">
              <a
                href="/#"
                onClick={() => {
                  console.log(this.state.eventID);
                }}
              >
                <i className="mdi mdi-pencil font-size-18"></i>
              </a>
            </Link>
            <Link to="#" className="text-danger">
              <i className="mdi mdi-trash-can font-size-18"></i>
            </Link>
          </>
        ),
      },
    ];
    init("user_RJ68i4JVspeNLQG0yX2fv");

    const defaultSorted = [
      {
        dataField: "id",
        order: "asc",
      },
    ];

    const pageOptions = {
      sizePerPage: 10,
      totalSize: adminusers.length, // replace later with size(customers),
      custom: true,
    };

    // Select All Button operation
    const selectRow = {
      mode: "checkbox",
    };
    const sendEmail = (e) => {
      e.preventDefault();
      console.log("WORKS");
      emailjs.send("service-01", "template_l4f4xuo").then(
        function (response) {
          console.log("SUCCESS!", response.status, response.text);
        },
        function (error) {
          console.log("FAILED...", error);
        }
      );
    };

    const { SearchBar } = Search;

    return (
      <React.Fragment>
        <div className="page-content">
          <div className="container-fluid">
            <Breadcrumbs
              title="Admin Users"
              breadcrumbItems={this.state.breadcrumbItems}
            />
            <Row>
              <Col className="col-12">
                <Card>
                  <CardBody>
                    <PaginationProvider
                      pagination={paginationFactory(pageOptions)}
                      keyField="id"
                      columns={columns}
                      data={this.state.productData}
                    >
                      {({ paginationProps, paginationTableProps }) => (
                        <ToolkitProvider
                          keyField="id"
                          columns={columns}
                          data={this.state.productData}
                          search
                        >
                          {(toolkitProps) => (
                            <React.Fragment>
                              <Row className="mb-2">
                                <Col md="4">
                                  <div className="search-box me-2 mb-2 d-inline-block">
                                    <div className="position-relative">
                                      <SearchBar
                                        {...toolkitProps.searchProps}
                                      />
                                      <i className="search-box chat-search-box" />
                                    </div>
                                  </div>
                                </Col>
                                <Col sm="8">
                                  <div className="text-sm-end">
                                    <div
                                      className="btn-group"
                                      role="group"
                                      aria-label="Basic radio toggle button group"
                                    >
                                      <input
                                        type="radio"
                                        className="btn-check"
                                        name="btnradio"
                                        id="btnradio1"
                                        autoComplete="off"
                                        defaultChecked
                                      />
                                      <label
                                        className="btn btn-outline-secondary"
                                        htmlFor="btnradio1"
                                      >
                                        Admin
                                      </label>

                                      <input
                                        type="radio"
                                        className="btn-check"
                                        name="btnradio"
                                        id="btnradio2"
                                        autoComplete="off"
                                      />
                                      <label
                                        className="btn btn-outline-secondary"
                                        htmlFor="btnradio2"
                                      >
                                        Author
                                      </label>
                                      <div className="text-sm-end">
                                        <div className="my-4 text-center">
                                          <Button
                                            type="button"
                                            color="primary"
                                            className="waves-effect waves-light"
                                            onClick={this.tog_center}
                                          >
                                            Invite Admin User
                                          </Button>
                                        </div>

                                        <Modal
                                          isOpen={this.state.modal_center}
                                          toggle={this.tog_center}
                                          centered={true}
                                        >
                                          <ModalHeader
                                            toggle={() =>
                                              this.setState({
                                                modal_center: false,
                                              })
                                            }
                                          >
                                            Center Modal
                                          </ModalHeader>
                                          <ModalBody>
                                            <p>
                                              Cras mattis consectetur purus sit
                                              amet fermentum. Cras justo odio,
                                              dapibus ac facilisis in, egestas
                                              eget quam. Morbi leo risus, porta
                                              ac consectetur ac, vestibulum at
                                              eros.
                                            </p>
                                            <p>
                                              Praesent commodo cursus magna, vel
                                              scelerisque nisl consectetur et.
                                              Vivamus sagittis lacus vel augue
                                              laoreet rutrum faucibus dolor
                                              auctor.
                                            </p>
                                            <p className="mb-0">
                                              Aenean lacinia bibendum nulla sed
                                              consectetur. Praesent commodo
                                              cursus magna, vel scelerisque nisl
                                              consectetur et. Donec sed odio
                                              dui. Donec ullamcorper nulla non
                                              metus auctor fringilla.
                                            </p>
                                          </ModalBody>
                                        </Modal>

                                        <Button
                                          type="button"
                                          color="success"
                                          className="btn-rounded mb-2 me-2"
                                          onClick={sendEmail}
                                          style={{ marginLeft: "5px" }}
                                        >
                                          Invite Author User
                                        </Button>
                                      </div>
                                    </div>
                                  </div>
                                </Col>
                              </Row>

                              <Row>
                                <Col xl="12">
                                  <div className="table-responsive">
                                    <BootstrapTable
                                      keyField={"id"}
                                      responsive
                                      bordered={false}
                                      striped={false}
                                      defaultSorted={defaultSorted}
                                      selectRow={selectRow}
                                      classes={
                                        "table align-middle table-nowrap"
                                      }
                                      headerWrapperClasses={"thead-light"}
                                      cellEdit={cellEditFactory({
                                        mode: "click",
                                      })}
                                      {...toolkitProps.baseProps}
                                      {...paginationTableProps}
                                    />
                                  </div>
                                </Col>
                              </Row>

                              <Row className="align-items-md-center mt-30">
                                <Col className="inner-custom-pagination d-flex">
                                  <div className="d-inline">
                                    <SizePerPageDropdownStandalone
                                      {...paginationProps}
                                    />
                                  </div>
                                  <div className="text-md-right ms-auto">
                                    <PaginationListStandalone
                                      {...paginationProps}
                                    />
                                  </div>
                                </Col>
                              </Row>
                            </React.Fragment>
                          )}
                        </ToolkitProvider>
                      )}
                    </PaginationProvider>
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default AdminUser;
