import React, { useState, createContext, useEffect } from "react";
import Axios from "axios";

export const ArticlesContext = createContext();

export const ArticlesProvider = (props) => {
  const [articles, setArticles] = useState([]);

  const apiUrl = "http://18.216.174.90:3000/api/article";
  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..qE3laVdfceQNTHjnh2ZyvR8kVlBe0bUSge0VNMdFGo9mYxdBZdNTRmRDDs5xJeZlO92yFjWZdm0hbHPu4Le5x_lV1G-1UN03QPEYspPPL7LGmZlrbY4SJW_shWiAI2OsnchdR6n3GOgP29v2ik6eNYZ63UmBJSfvbwNHwtYtGJ5wOb-aLoSKuVPcIVALog8FaOacUvWddDWCrktgNat78AxfyoW1TB_J5Q0FEMCLQYv8jp68eQYAQok2HIBQP0r99CxV-97N6Yt2oJSKOAOgpt_1Ob9AAag1SV3ueExg6mKGdXBNm4o2e5tijrog8vvcn0X0isaE3mcgXS68gjDSDA";

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
  // const response = Axios.get(apiUrl, config);

  useEffect(() => {
    Axios.get(apiUrl, config).then((response) => setArticles(response.data));
  }, []);

  const [addNew, setAddNew] = useState(false);

  const [columns, setColumns] = useState([
    {
      title: "Header",
    },
    {
      title: "Author",
    },
    {
      title: "Likes",
    },
    {
      title: "Views",
    },
    {
      title: "Comments",
    },
    {
      title: "Featured",
    },
    {
      title: "Action",
    },
  ]);

  return (
    <ArticlesContext.Provider
      value={{
        articles: [articles, setArticles],
        addNew: [addNew, setAddNew],
        columns: [columns, setColumns],
      }}
    >
      {props.children}
    </ArticlesContext.Provider>
  );
};
