import React, { useState } from "react";
import "./AddPlace.css";

import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import Button from "@mui/material/Button";

import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import IconButton from "@mui/material/IconButton";
import PhotoCamera from "@mui/icons-material/PhotoCamera";

const AddPlace = () => {
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [longituide, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [category, setCategory] = useState("");
  const [phone, setPhone] = useState("");
  const [fb, setFb] = useState("");
  const [insta, setInsta] = useState("");
  const [twitter, setTwitter] = useState("");
  const [url, setUrl] = useState("");
  const [email, setEmail] = useState("");
  const [file, setFile] = useState(null);
  const [s3File, setS3File] = useState("");
  const [createFieldsFilled, setCreateFieldsFilled] = useState(null);

  const checkCreateFields = () => {
    if (
      title === "" ||
      address === "" ||
      longituide === "" ||
      latitude === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      phone === "" ||
      fb === "" ||
      insta === "" ||
      twitter === "" ||
      url === "" ||
      email === "" ||
      file === null
    ) {
      setCreateFieldsFilled(true);
    } else {
      setCreateFieldsFilled(false);
    }
  };

  const buttonStyle = {
    backgroundColor: "#1ca395",
  };

  return (
    <div className="addplace-container">
      <h1 style={{ color: "#343a40" }}> Add a Pet Friendly Place </h1>
      <div style={{ display: "flex" }}>
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "60ch" },
            display: "flex",
            flexDirection: "column",
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="outlined-basic"
            label="Title"
            variant="outlined"
            onChange={(e) => setTitle(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Address"
            variant="outlined"
            onChange={(e) => setAddress(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Longitude"
            variant="outlined"
            onChange={(e) => setLongitude(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Latitude"
            variant="outlined"
            onChange={(e) => setLatitude(e.target.value)}
          />
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <input
              type="datetime-local"
              className="datepicker"
              onChange={(e) => setStartTime(e.target.value)}
            />
            <input
              type="datetime-local"
              className="datepicker"
              onChange={(e) => setEndTime(e.target.value)}
            />
          </div>
        </Box>
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "60ch" },
            display: "flex",
            flexDirection: "column",
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="outlined-basic"
            label="Postal Code"
            variant="outlined"
            onChange={(e) => setPostalCode(e.target.value)}
          />
          <FormControl sx={{ m: 1, minWidth: 120 }}>
            <InputLabel id="demo-simple-select-helper-label">
              Category
            </InputLabel>
            <Select
              labelId="demo-simple-select-helper-label"
              id="demo-simple-select-helper"
              label="Age"
              onChange={(e) => setCategory(e.target.value)}
            >
              <MenuItem value="Restaurant">Restaurant</MenuItem>
              <MenuItem value="Cafe">Cafe</MenuItem>
              <MenuItem value="Pub">Pub</MenuItem>
              <MenuItem value="Park">Park</MenuItem>
              <MenuItem value="Hotel">Hotel</MenuItem>
            </Select>
          </FormControl>
          <TextField
            id="outlined-basic"
            label="Phone"
            variant="outlined"
            onChange={(e) => setPhone(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            label="Email"
            variant="outlined"
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            id="outlined-start-adornment"
            sx={{ m: 1, width: "25ch" }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">https://</InputAdornment>
              ),
            }}
            onChange={(e) => setUrl(e.target.value)}
          />
          <label htmlFor="contained-button-file">
            <Input
              accept="image/*"
              id="contained-button-file"
              multiple
              type="file"
            />
            <Button variant="contained" component="span">
              Upload
            </Button>
          </label>
        </Box>
      </div>
      <div style={{ display: "flex", marginTop: "20px", marginLeft: "10px" }}>
        <Button style={buttonStyle} variant="contained">
          Add Place
        </Button>
      </div>
    </div>
  );
};

export default AddPlace;
