import React, { useState, Fragment, useContext } from "react";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";

import ReadOnly from "./ReadOnly";
import EditableData from "./EditableData";

import { PlacesContext } from "../PlacesContext";

import Axios from "axios";

import "./DataTables.css";

const DataTable = () => {
  const { places, addNew, columns } = useContext(PlacesContext);
  const [placesValue, setPlacesValue] = places;
  const [addNewValue, setAddNewValue] = addNew;
  const [columnsValue, setcolumnsvalue] = columns;

  //USR DATA

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [longituide, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [category, setCategory] = useState("");
  const [phone, setPhone] = useState("");
  const [fb, setFb] = useState("");
  const [insta, setInsta] = useState("");
  const [twitter, setTwitter] = useState("");
  const [url, setUrl] = useState("");
  const [email, setEmail] = useState("");

  const [searchParam, setSearchParam] = useState("");
  const [editContactId, setEditContactId] = useState(null);

  const [fieldsFilled, setFieldsFilled] = useState(null);

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      longituide === "" ||
      latitude === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      phone === "" ||
      fb === "" ||
      insta === "" ||
      twitter === "" ||
      url === "" ||
      email === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };

  const styles = {
    borderColor: "red",
  };

  const handleEditClick = (event, product) => {
    event.preventDefault();
    setEditContactId(product.id);
  };

  const filteredData = placesValue.filter((place) =>
    place.name.toLowerCase().includes(searchParam.toLowerCase())
  );

  var today = new Date();
  var date =
    today.getFullYear() + "-" + (today.getMonth() + 1) + "-" + today.getDate();
  var time =
    today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
  var dateTime = date.toString + " " + time.toString;
  console.log(dateTime);

  const bodyParameters = {
    pic: {
      pic: [
        {
          url: "760634d2-f8ec-4caa-9a2d-14a8828cfb5dpetplace.jpg",
        },
        {
          url: "760634d2-f8ec-4caa-9a2d-14a8828cfb5dpetplace.jpg",
        },
      ],
    },
    title: { title },
    title_icon: "760634d2-f8ec-4caa-9a2d-14a8828cfb5dpetplace.jpg",
    social_link: {
      social_link: [
        {
          facebook: { fb },
        },
        {
          instagram: { insta },
        },
      ],
    },
    phone_number: { phone },
    address: { address },
    lat: { latitude },
    long: { longituide },
    postcode: { postalCode },
    start_time: "2021-11-15T12:15:39.658Z",
    end_time: "2021-11-15T12:15:39.658Z",
    email: { email },
    web_url: { url },
    createdAt: "2021-11-15T12:15:39.658Z",
    category: 1,
  };

  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..pTCOwqmlHOx0I3qayOsP4dneUKAblpLCOXcOKCx50b6dg2InJz5_58oENh4_XJIzHubOBN0LbAH77D9W3zYdUl-i46qGkDfOSy5xf-mTS_yHD7LeoauhmwTHHcMG9ieYxqK7Mgb9cUHU2-IfX29zHTYTCGroqH_Zluk04UI4yItpqcUHF7q7dviZPPC5dHhJpTzPyGnGF9vDkEKfsD6gKRRGUjkWBySAv-gjH-JVL-qOvhrfFbYyIpPjOSUMYexJCtetfP-9Wh7CVTRiu9Inr7XKuu8vWsqMY6yemMLTSwevnKx82gyDQro2anclcG-sA0Lze-TQQJx8bryAg7fOIg";

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const apiUrl = "http://18.216.174.90:3000/api/petplace";

  const sendUrl = (e) => {
    e.preventDefault();
    checkFields();
    // if (fieldsFilled === true) {
    console.log("RECEIVED");
    Axios.post(apiUrl, bodyParameters, config)
      .then((response) => console.log(response))
      .catch(console.log);
  };

  return (
    <div className="app-container">
      {!addNewValue ? (
        <div>
          <div className="table-top">
            <Input
              placeholder="Search the table by name..."
              style={{ width: "25%", borderRadius: "30px", height: "40px" }}
              onChange={(e) => setSearchParam(e.target.value)}
            />
            <Button
              type="button"
              color="success"
              className="btn-rounded mb-2 me-2"
              onClick={() => {
                setAddNewValue(true);
              }}
            >
              <i className="mdi mdi-plus me-1" /> Add Place
            </Button>
          </div>
          <div style={{ width: "100%", overflow: "auto" }}>
            <table style={{ fontSize: "5px" }}>
              <thead>
                {columnsValue.map((header) => (
                  <th> {header.title} </th>
                ))}
              </thead>
              <tbody>
                {filteredData.map((product) => (
                  <Fragment>
                    {editContactId === product.id ? (
                      <EditableData data={product} />
                    ) : (
                      <ReadOnly
                        data={product}
                        handleEditClick={handleEditClick}
                      />
                    )}
                  </Fragment>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="input-add-events">
          <Container fluid>
            <Row>
              <Col xs={12}>
                <Card>
                  <CardBody>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-text-input"
                        className="col-md-2 col-form-label"
                      >
                        Title*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            title === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Title"
                          }
                          id="title"
                          onChange={(e) => setTitle(e.target.value)}
                          style={
                            title === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Address*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            address === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Address"
                          }
                          id="title"
                          onChange={(e) => setAddress(e.target.value)}
                          style={
                            address === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Longitude & Latitude*
                      </Label>
                      <Col md={5}>
                        <Input
                          type="text"
                          placeholder={
                            longituide === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Longitude"
                          }
                          id="example-search-input"
                          onChange={(e) =>
                            setLongitude(e.target.value.toString)
                          }
                          style={
                            longituide === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                      <Col md={5}>
                        <Input
                          type="text"
                          placeholder={
                            latitude === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Latitude"
                          }
                          id="example-search-input"
                          onChange={(e) => setLatitude(e.target.value.toString)}
                          style={
                            latitude === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-time-input"
                        className="col-md-2 col-form-label"
                      >
                        Start Time*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="time"
                          placeholder={
                            startTime === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Start Time"
                          }
                          id="example-time-input"
                          onChange={(e) =>
                            setStartTime(e.target.value.toString())
                          }
                          style={
                            startTime === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-time-input"
                        className="col-md-2 col-form-label"
                      >
                        End Time*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="time"
                          placeholder={
                            endTime === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter End Time"
                          }
                          id="example-time-input"
                          onChange={(e) =>
                            setEndTime(e.target.value.toString())
                          }
                          style={
                            endTime === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Postal Code*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            postalCode === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Postal Code"
                          }
                          id="example-search-input"
                          onChange={(e) => setPostalCode(e.target.value)}
                          style={
                            postalCode === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>

                    <Row className="mb-3">
                      <Label className="col-md-2 col-form-label">
                        Category*
                      </Label>
                      <Col md={10}>
                        <select
                          className="form-control"
                          onChange={(e) => setCategory(e.target.value)}
                          style={
                            (category === "" ||
                              category === "Select a Category") &&
                            fieldsFilled === false
                              ? styles
                              : null
                          }
                        >
                          <option defaultValue>Select a Category</option>
                          <option value="Resturant">Resturant</option>
                          <option value="Cafe">Cafe</option>
                          <option value="Pub">Pub</option>
                          <option value="Park">Park</option>
                          <option value="Hotel">Hotel</option>
                        </select>
                      </Col>
                    </Row>

                    <Row className="mb-3">
                      <Label
                        htmlFor="example-tel-input"
                        className="col-md-2 col-form-label"
                      >
                        Phone*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="tel"
                          placeholder={
                            phone === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Phone"
                          }
                          id="example-tel-input"
                          onChange={(e) => setPhone(e.target.value)}
                          style={
                            phone === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-url-input"
                        className="col-md-2 col-form-label"
                      >
                        Facebook Link*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            fb === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Facebook Link"
                          }
                          id="example-url-input"
                          onChange={(e) => setFb(e.target.value)}
                          style={
                            phone === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-url-input"
                        className="col-md-2 col-form-label"
                      >
                        Instagram Link*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            insta === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Instagram Link"
                          }
                          id="example-url-input"
                          onChange={(e) => setInsta(e.target.value)}
                          style={
                            insta === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-url-input"
                        className="col-md-2 col-form-label"
                      >
                        Twitter Link*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            twitter === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Twitter Link"
                          }
                          id="example-url-input"
                          onChange={(e) => setTwitter(e.target.value)}
                          style={
                            twitter === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-url-input"
                        className="col-md-2 col-form-label"
                      >
                        Email*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            email === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Email"
                          }
                          id="example-url-input"
                          onChange={(e) => setEmail(e.target.value)}
                          style={
                            email === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-url-input"
                        className="col-md-2 col-form-label"
                      >
                        URL Web*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            url === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter URL"
                          }
                          id="example-url-input"
                          onChange={(e) => setUrl(e.target.value)}
                          style={
                            url === "" && fieldsFilled === false ? styles : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Upload Images*
                      </Label>
                      <Col md={10}>
                        <div className="input-group">
                          <input
                            type="file"
                            className="form-control"
                            id="customFile"
                          />
                        </div>
                      </Col>
                    </Row>
                    {fieldsFilled === false ? (
                      <p style={{ color: "red", textAlign: "center" }}>
                        One or more required fields are missing{" "}
                      </p>
                    ) : null}
                  </CardBody>
                  <div className="add-events-buttons">
                    <Button
                      color="light"
                      type="button"
                      className="waves-effect waves-light me-1"
                      outline
                      onClick={() => {
                        setFieldsFilled(true);
                        setAddNewValue(false);
                      }}
                    >
                      <i className="ri-close-line align-middle me-2"></i> Cancel
                    </Button>

                    <Button
                      color="success"
                      type="button"
                      className="waves-effect waves-light me-1"
                      onClick={sendUrl}
                    >
                      <i className="ri-add-circle-fill align-middle ms-2"></i>
                      Add Place
                    </Button>
                  </div>
                </Card>
              </Col>
            </Row>
          </Container>
        </div>
      )}
    </div>
  );
};

export default DataTable;
