import React, { useState } from "react";
import { Card, Col, Row, Button, Label, Input } from "reactstrap";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

const AdminUser = () => {
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [email, setEmail] = useState("");

  const sender = "husnain.mehmood@neuralsoftsolutions.com";

  const sgMail = require("@sendgrid/mail");
  sgMail.setApiKey(
    "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"
  );
  const msg = {
    to: "m.husnainmehmood@gmail.com", // Change to your recipient
    from: "husnain.mehmood@neuralsoftsolutions.com", // Change to your verified sender
    subject: "Create your account!",
    text: `Hello Go to http://localhost:3002/inviteadmin to create your account.`,
    html: "<strong>and easy to do anywhere, even with Node.js</strong>",
  };

  const sendMail = () => {
    sgMail
      .send(msg)
      .then(() => {
        console.log("Email sent");
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <React.Fragment>
      <div className="page-content">
        <div
          className="container-fluid"
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "auto",
          }}
        >
          <Col className="col-10">
            <Card style={{ padding: "50px" }}>
              <h1
                style={{
                  fontFamily: "Avenir",
                  alignItems: "center",
                  marginBottom: "50px",
                }}
              >
                Invite an Admin
              </h1>
              <Row className="mb-3">
                <Label
                  htmlFor="example-url-input"
                  className="col-md-2 col-form-label"
                >
                  First Name
                </Label>
                <Col md={10}>
                  <Input
                    type="text"
                    placeholder="First Name"
                    id="example-url-input"
                    onChange={(e) => setFirstname(e.target.value)}
                  />
                </Col>
              </Row>
              <Row className="mb-3">
                <Label
                  htmlFor="example-url-input"
                  className="col-md-2 col-form-label"
                >
                  Last Name
                </Label>
                <Col md={10}>
                  <Input
                    type="text"
                    placeholder="Last Name"
                    id="example-url-input"
                    onChange={(e) => setLastname(e.target.value)}
                  />
                </Col>
              </Row>

              <Row className="mb-3">
                <Label
                  htmlFor="example-url-input"
                  className="col-md-2 col-form-label"
                >
                  Email
                </Label>
                <Col md={10}>
                  <Input
                    type="email"
                    placeholder="Enter Email"
                    id="example-url-input"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </Col>
              </Row>

              <div
                style={{
                  marginTop: "25px",
                  display: "flex",
                  justifyContent: "flex-end",
                }}
              >
                <Button
                  color="light"
                  type="button"
                  className="waves-effect waves-light me-1"
                >
                  <i className="ri-close-line align-middle me-2"></i> Cancel
                </Button>
                <Button
                  color="success"
                  type="button"
                  className="waves-effect waves-light me-1"
                  style={{ marginLeft: "10px" }}
                  onClick={sendMail}
                >
                  <i className="ri-mail-send-line align-middle me-2"></i> Send
                  the Invite
                </Button>
              </div>
            </Card>
          </Col>
        </div>
      </div>
    </React.Fragment>
  );
};

export default AdminUser;
