import React, { useState } from "react";

const EditableData = ({ data, id }) => {
  const styles = {
    backgroundColor: "#EEEEEE",
    border: "none",
    height: "30px",
  };
  const errStyles = {
    borderColor: "red",
  };

  const [fieldsFilled, setFieldsFilled] = useState(null);

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");

  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [category, setCategory] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      url === "" ||
      details === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };

  return (
    <tr>
      <td>
        <input
          required
          placeholder="Enter the title..."
          type="text"
          style={title === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setTitle(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the address..."
          type="text"
          style={address === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setTitle(e.target.value)}
        />
      </td>
      <td>
        <select
          className="form-control"
          onChange={(e) => setCategory(e.target.value)}
          style={
            (category === "" || category === "Select a Category") &&
            !fieldsFilled
              ? styles
              : null
          }
        >
          <option defaultValue>Select</option>
          <option value="Resturant">Online</option>
          <option value="Cafe">Offline</option>
        </select>
      </td>
      <td>
        <input
          required
          placeholder="Enter the age..."
          type="text"
          style={styles}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the start date..."
          type="text"
          style={styles}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the salary..."
          type="text"
          style={styles}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the name..."
          type="text"
          style={styles}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the position..."
          type="text"
          style={styles}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the office..."
          type="text"
          style={styles}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the age..."
          type="text"
          style={styles}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the start date..."
          type="text"
          style={styles}
        />
      </td>
    </tr>
  );
};

export default EditableData;
