import React, { useState, Fragment, useContext } from "react";
import { UncontrolledAlert } from "reactstrap";
import Axios from "axios";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";

import ReadOnly from "./ReadOnly";

import { EventsContext } from "../PetEventsContext";

import "./DataTables.css";

const DataTable = () => {
  const { events, state, columns } = useContext(EventsContext);
  const [eventsValue, setEventsValue] = events;
  const [stateValue, setStateValue] = state;
  const columnsValue = columns;

  //USR DATA

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");
  const [category, setCategory] = useState("");

  const [file, setFile] = useState([]);
  const [s3File, setS3File] = useState("");

  const [searchParam, setSearchParam] = useState("");
  const [editEventId, setEditEventId] = useState(null);

  const [fieldsFilled, setFieldsFilled] = useState(null);
  const [deleteSuccess, setDeleteSuccess] = useState(null);

  const deleteEventStatus = (e) => {
    setDeleteSuccess(e);
  };
  const getEditId = (e) => {
    setEditEventId(e);
  };

  const bodyParameters = {
    pic: s3File,
    title: title,
    start_time: "2021-11-15T12:15:39.658Z",
    end_time: "2021-11-15T12:15:39.658Z",
    postal_code: postalCode,
    location: address,
    url: url,
    event_type: category,
    detail: details,
    createdAt: "2021-11-15T12:15:39.658Z",
  };

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
