import React, { Component } from "react";
import {
  Form,
  Card,
  CardBody,
  Col,
  Row,
  Button,
  Container,
  Input,
} from "reactstrap";
import { Editor } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

import { Link } from "react-router-dom";
import places from "./places.json";
// datatable related plugins
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
  PaginationListStandalone,
  SizePerPageDropdownStandalone,
} from "react-bootstrap-table2-paginator";

import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import "./datatables.scss";
import "./Articles.scss";

// Table data

class Articles extends Component {
  constructor(props) {
    super(props);
    this.state = {
      breadcrumbItems: [
        { title: "Tables", link: "#" },
        { title: "Data Table", link: "#" },
      ],
      page: 1,
      sizePerPage: 10,
      productData: places,
      addNew: false,
      eventID: "",
      title: "",
      startDate: "",
      endDate: "",
      time: "",
      address: "",
      link: "",
      details: "",
      uploadedImage: null,
    };

    this.handleStart = this.handleStart.bind(this);
    this.handleEnd = this.handleEnd.bind(this);
  }

  handleDefault(date) {
    this.setState({ default_date: date });
  }
  handleStart = (date) => {
    this.setState({ start_date: date });
  };
  handleEnd = (date) => {
    this.setState({ end_date: date });
  };
  handleMonthChange(date) {
    this.setState({ monthDate: date });
  }
  handleYearChange(date) {
    this.setState({ yearDate: date });
  }
  handleFileSelect = (event) => {
    this.setState(this.setState({ uploadedImage: event.target.files[0] }));
  };

  render() {
    const columns = [
      {
        dataField: "ID",
        text: "ID",
        sort: true,
      },
      {
        dataField: "title",
        text: "Title",
        sort: false,
      },
      {
        dataField: "category",
        text: "Category",
        sort: true,
      },
      {
        dataField: "author",
        text: "Author",
        sort: true,
      },
      {
        dataField: "date",
        text: "Date",
        sort: true,
      },

      {
        dataField: "menu",
        isDummyField: true,
        text: "Action",
        formatter: () => (
          <>
            <Link to="#" className="me-3 text-primary">
              <a
                href="/#"
                onClick={() => {
                  console.log(this.state.eventID);
                }}
              >
                <i className="mdi mdi-pencil font-size-18"></i>
              </a>
            </Link>
            <Link to="#" className="text-danger">
              <i className="mdi mdi-trash-can font-size-18"></i>
            </Link>
          </>
        ),
      },
    ];

    const defaultSorted = [
      {
        dataField: "id",
        order: "asc",
      },
    ];

    const pageOptions = {
      sizePerPage: 10,
      totalSize: places.length, // replace later with size(customers),
      custom: true,
    };

    // Select All Button operation
    const selectRow = {
      mode: "checkbox",
    };

    const { SearchBar } = Search;

    return (
      <React.Fragment>
        <div className="page-content">
          {!this.state.addNew ? (
            <div className="container-fluid">
              <Breadcrumbs
                title="Articles"
                breadcrumbItems={this.state.breadcrumbItems}
              />

              <Row>
                <Col className="col-12">
                  <Card>
                    <CardBody>
                      <PaginationProvider
                        pagination={paginationFactory(pageOptions)}
                        keyField="id"
                        columns={columns}
                        data={this.state.productData}
                      >
                        {({ paginationProps, paginationTableProps }) => (
                          <ToolkitProvider
                            keyField="id"
                            columns={columns}
                            data={this.state.productData}
                            search
                          >
                            {(toolkitProps) => (
                              <React.Fragment>
                                <Row className="mb-2">
                                  <Col md="4">
                                    <div className="search-box me-2 mb-2 d-inline-block">
                                      <div className="position-relative">
                                        <SearchBar
                                          {...toolkitProps.searchProps}
                                        />
                                        <i className="search-box chat-search-box" />
                                      </div>
                                    </div>
                                  </Col>
                                  <Col sm="8">
                                    <div className="text-sm-end">
                                      <Button
                                        type="button"
                                        color="success"
                                        className="btn-rounded mb-2 me-2"
                                        onClick={() =>
                                          this.setState({ addNew: true })
                                        }
                                      >
                                        <i className="mdi mdi-plus me-1" /> Add
                                        Articles
                                      </Button>
                                    </div>
                                  </Col>
                                </Row>

                                <Row>
                                  <Col xl="12">
                                    <div className="table-responsive">
                                      <BootstrapTable
                                        keyField={"id"}
                                        responsive
                                        bordered={false}
                                        striped={false}
                                        defaultSorted={defaultSorted}
                                        selectRow={selectRow}
                                        classes={
                                          "table align-middle table-nowrap"
                                        }
                                        headerWrapperClasses={"thead-light"}
                                        {...toolkitProps.baseProps}
                                        {...paginationTableProps}
                                      />
                                    </div>
                                  </Col>
                                </Row>

                                <Row className="align-items-md-center mt-30">
                                  <Col className="inner-custom-pagination d-flex">
                                    <div className="d-inline">
                                      <SizePerPageDropdownStandalone
                                        {...paginationProps}
                                      />
                                    </div>
                                    <div className="text-md-right ms-auto">
                                      <PaginationListStandalone
                                        {...paginationProps}
                                      />
                                    </div>
                                  </Col>
                                </Row>
                              </React.Fragment>
                            )}
                          </ToolkitProvider>
                        )}
                      </PaginationProvider>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </div>
          ) : (
            <div className="input-add-articles">
              <Container fluid>
                <Breadcrumbs
                  title="Add an Article"
                  breadcrumbItems={this.state.breadcrumbItems}
                />
                <Row>
                  <Col>
                    <Card>
                      <CardBody>
                        <Row className="mb-3">
                          <Col md={12}>
                            <Input
                              type="text"
                              placeholder="Add Title"
                              id="example-search-input"
                            />
                          </Col>
                        </Row>
                        <Row className="mb-3">
                          <Col md={5}>
                            <select className="form-control">
                              <option defaultValue>Select a Category</option>
                              <option value="1">Article</option>
                              <option value="2">Blog</option>
                            </select>
                          </Col>
                          <Col md={5}>
                            <Input
                              type="text"
                              placeholder="Add Author"
                              id="example-search-input"
                            />
                          </Col>
                        </Row>

                        <Form method="post">
                          <Editor
                            toolbarClassName="toolbarClassName"
                            wrapperClassName="wrapperClassName"
                            editorClassName="editorClassName"
                          />
                        </Form>
                      </CardBody>
                      <div className="add-events-buttons">
                        <Button
                          color="light"
                          type="button"
                          className="waves-effect waves-light me-1"
                          outline
                          onClick={() => this.setState({ addNew: false })}
                        >
                          <i className="ri-close-line align-middle me-2"></i>{" "}
                          Cancel
                        </Button>

                        <Button
                          color="success"
                          type="button"
                          className="waves-effect waves-light me-1"
                          onClick={() => {
                            places.push({
                              eventID: places.length + 1,
                              title: this.state.title,
                              startDate: this.state.startDate,
                              endDate: this.state.endDate,
                              time: this.state.time,
                              address: this.state.address,
                              link: this.state.URL,
                              details: this.state.details,
                              uploadedImage: "Image not available",
                            });
                            localStorage.setItem(
                              "products",
                              JSON.stringify(places)
                            );
                            console.log(places);
                            this.setState({ addNew: false });
                          }}
                        >
                          <i className="ri-add-circle-fill align-middle ms-2"></i>
                          Add Article
                        </Button>
                      </div>
                    </Card>
                  </Col>
                </Row>
              </Container>
            </div>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default Articles;
