import React, { useState, useEffect, Fragment } from "react";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TablePagination from "@mui/material/TablePagination";
import TableRow from "@mui/material/TableRow";
import Axios from "axios";
import IconButton from "@mui/material/IconButton";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";

import { Link } from "react-router-dom";
import Button from "@mui/material/Button";
import AddIcon from "@mui/icons-material/Add";
import TextField from "@mui/material/TextField";

const columns = [
  { id: "title", label: "Title", minWidth: 100 },
  { id: "address", label: "Address", minWidth: 100 },
  { id: "long", label: "Longitude", minWidth: 100 },
  { id: "lat", label: "Latitude", minWidth: 100 },
  { id: "open", label: "Open Time", minWidth: 100 },
  { id: "close", label: "Close Time", minWidth: 100 },
  { id: "postal", label: "Postal Code", minWidth: 100 },
  { id: "category", label: "Category", minWidth: 100 },
  { id: "phone", label: "Phone", minWidth: 100 },
  { id: "email", label: "Email", minWidth: 100 },
  { id: "url", label: "URL", minWidth: 100 },
  { id: "action", label: "Actions", minWidth: 100 },
];

export default function StickyHeadTable({ token, apiUrl }) {
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(50);
  const [petPlaces, setPetPlaces] = useState([]);
  const [search, setSearch] = useState("");

  // EDIT DATA
  const [table, setTable] = useState(true);
  const [id, setId] = useState("");
  const [titleE, setTitleE] = useState("");
  const [addressE, setAddressE] = useState("");
  const [longituideE, setLongitudeE] = useState("");
  const [latitudeE, setLatitudeE] = useState("");
  const [startTimeE, setStartTimeE] = useState("");
  const [endTimeE, setEndTimeE] = useState("");
  const [postalCodeE, setPostalCodeE] = useState("");
  const [categoryE, setCategoryE] = useState("");
  const [phoneE, setPhoneE] = useState("");
  const [fbE, setFbE] = useState("");
  const [instaE, setInstaE] = useState("");
  const [twitterE, setTwitterE] = useState("");
  const [urlE, setUrlE] = useState("");
  const [emailE, setEmailE] = useState("");

  const placesApi = `${apiUrl}/petplace`;

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  useEffect(() => {
    Axios.get(placesApi, config).then((response) =>
      setPetPlaces(response.data)
    );
  }, []);

  const filteredPlaces = petPlaces.filter((place) =>
    place.title.toLowerCase().includes(search.toLowerCase())
  );
  const buttonStyle = {
    backgroundColor: "#1ca395",
  };
  const searchStyle = {
    width: "350px",
  };

  return (
    <Fragment>
      {table ? (
        <Fragment>
          <h1 style={{ color: "#343a40" }}> Pet Places </h1>
          <div className="places-actions">
            <TextField
              label="Search the places..."
              sx={searchStyle}
              onChange={(e) => setSearch(e.target.value)}
            />
            <Link to="/places/add-place" style={{ textDecoration: "none" }}>
              <Button
                sx={buttonStyle}
                variant="contained"
                startIcon={<AddIcon />}
              >
                Add Place
              </Button>
            </Link>
          </div>
          <Paper sx={{ width: "100%", height: "100%", overflow: "hidden" }}>
            <TableContainer sx={{ maxHeight: 440 }}>
              <Table stickyHeader aria-label="sticky table">
                <TableHead>
                  <TableRow>
                    {columns.map((column) => (
                      <TableCell
                        key={column.id}
                        align={column.align}
                        style={{ minWidth: column.minWidth }}
                      >
                        {column.label}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {filteredPlaces
                    .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                    .map((place, idx) => {
                      return (
                        <TableRow
                          hover
                          role="checkbox"
                          tabIndex={-1}
                          key={place.id}
                        >
                          <TableCell>{place.title}</TableCell>
                          <TableCell>{place.address}</TableCell>
                          <TableCell>{place.long}</TableCell>
                          <TableCell>{place.lat}</TableCell>
                          <TableCell>
                            {place.start_time.substr(11, 5)}
                          </TableCell>
                          <TableCell>{place.end_time.substr(11, 5)}</TableCell>
                          <TableCell>{place.postcode}</TableCell>
                          <TableCell>{place.category.name}</TableCell>
                          <TableCell>{place.phone_number}</TableCell>
                          <TableCell>{place.email}</TableCell>
                          <TableCell>{place.web_url}</TableCell>
                          <TableCell>
                            <div>
                              <IconButton
                                aria-label="delete"
                                fontSize="small"
                                color="error"
                                onClick={(e) => {
                                  e.preventDefault();
                                  Axios.delete(`${placesApi}/${place.id}`, {
                                    headers: {
                                      Authorization: `Bearer ${token}`,
                                    },
                                  })
                                    .then((res) => {
                                      console.log(res);
                                      window.location.reload(true);
                                    })
                                    .catch((err) => console.log(err));
                                }}
                              >
                                <DeleteIcon />
                              </IconButton>

                              <IconButton
                                aria-label="delete"
                                fontSize="small"
                                color="primary"
                                onClick={() => setTable(false)}
                              >
                                <EditIcon />
                              </IconButton>
                            </div>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[50, 100, 150]}
              component="div"
              count={petPlaces.length}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
            />
          </Paper>
        </Fragment>
      ) : null}
    </Fragment>
  );
}
