import React, { Component, useState } from "react";
import { Row, Col, Button, Container, Label } from "reactstrap";

import { CognitoUserPool, CognitoUser } from "amazon-cognito-identity-js";
// availity-reactstrap-validation
import { AvForm, AvField } from "availity-reactstrap-validation";
// action

import {
  registerUser,
  registerUserFailed,
  apiError,
} from "../../store/actions";
import "./RegisterAdmin.scss";

// Redux
import { Link } from "react-router-dom";

// import images
import logodark from "../../assets/images/logo-dark.png";

const RegisterAdmin = () => {
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [code, setCode] = useState("");

  const poolData = {
    UserPoolId: "ap-south-1_laNRncKhd",
    ClientId: "hd1ls0cm7qr9r6vaimtnvv4l0",
  };

  // const UserPool = new CognitoUserPool(this.poolData);

  const cognitoUser = new CognitoUser({
    Username: this.state.email,
    Pool: this.UserPool,
  });

  const signUp = (event) => {
    event.preventDefault();

    this.UserPool.signUp(email, password, [], null, (err, data) => {
      if (err) console.log(err);
      console.log(data);
      if (data) {
        this.setState({ step: "1" });
      }
    });
  };

  const confirmSignup = (event) => {
    event.preventDefault();
    this.UserPool.confirmRegistration(this.state.code, true, (err, result) => {
      if (err) {
        alert(err.message || JSON.stringify(err));
        return;
      }
    });
  };

  return (
    <React.Fragment>
      <div>
        <Container fluid className="p-0">
          <Row className="g-0">
            <Col lg={4}>
              <div className="authentication-page-content p-4 d-flex align-items-center min-vh-100">
                <div className="w-100">
                  <Row className="justify-content-center">
                    <Col lg={9}>
                      <div>
                        <div className="text-center">
                          <div>
                            <Link to="#" className="logo">
                              <img src={logodark} height="20" alt="logo" />
                            </Link>
                          </div>
                          {this.state.step === "0" ? (
                            <h4 className="font-size-18 mt-4">
                              Register Your Admin Account
                            </h4>
                          ) : (
                            <h4 className="font-size-18 mt-4">
                              Verify your Email
                            </h4>
                          )}
                        </div>
                        {this.state.step === "0" ? (
                          <div className="p-2 mt-5">
                            <AvForm
                              onValidSubmit={this.handleSubmit}
                              className="form-horizontal"
                            >
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-user-3-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Full Name</Label>
                                <AvField
                                  name="firstname"
                                  validate={{ required: true }}
                                  type="text"
                                  className="form-control"
                                  id="firstname"
                                  placeholder="Enter your full name"
                                  onChange={(e) => {
                                    this.setState({
                                      fullName: e.target.value,
                                    });
                                  }}
                                />
                              </div>

                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-mail-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Email</Label>
                                <AvField
                                  name="email"
                                  validate={{ email: true, required: true }}
                                  type="email"
                                  className="form-control"
                                  id="useremail"
                                  placeholder="Enter your email"
                                  onChange={(e) => {
                                    this.setState({ email: e.target.value });
                                  }}
                                />
                              </div>
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-phone-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Mobile</Label>
                                <AvField
                                  name="number"
                                  placeholder="Enter Only number"
                                  type="number"
                                  errorMessage="Enter Only Number"
                                  validate={{
                                    required: { value: true },
                                    pattern: {
                                      value: "^[0-9]+$",
                                      errorMessage: "Only Numbers",
                                    },
                                  }}
                                  onChange={(e) => {
                                    this.setState({ phone: e.target.value });
                                  }}
                                />
                              </div>
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-lock-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">Password</Label>
                                <AvField
                                  name="pass"
                                  type="password"
                                  className="form-control"
                                  id="userpassword"
                                  placeholder="Enter password"
                                  onChange={(e) => {
                                    this.setState({
                                      password: e.target.value,
                                    });
                                  }}
                                />
                              </div>
                              <div className="auth-form-group-custom mb-4">
                                <i className="ri-lock-line auti-custom-input-icon"></i>
                                <Label htmlFor="useremail">
                                  Confirm Password
                                </Label>
                                <AvField
                                  name="pass"
                                  type="password"
                                  className="form-control"
                                  id="userpassword"
                                  placeholder="Enter password"
                                />
                              </div>

                              <div className="text-center">
                                <Button
                                  color="primary"
                                  className="w-md waves-effect waves-light"
                                  type="submit"
                                  onClick={this.signUp}
                                >
                                  {this.props.loading
                                    ? "Loading ..."
                                    : "Register"}
                                </Button>
                              </div>

                              <div className="mt-4 text-center">
                                <p className="mb-0">
                                  By registering you agree to the Nazox
                                  <Link to="#" className="text-primary">
                                    {" "}
                                    Terms of Use
                                  </Link>
                                </p>
                                <p>
                                  Already have an account?{" "}
                                  <Link
                                    to="/login"
                                    className="fw-medium text-primary"
                                  >
                                    Login
                                  </Link>
                                </p>
                              </div>
                            </AvForm>
                          </div>
                        ) : (
                          <div className="verify-form">
                            <div className="verify-info-container">
                              <p
                                className="verify-info"
                                style={{ textAlign: "center" }}
                              >
                                We have sent an OTP (One Time Password) to you.
                                Please enter the code below to continue
                              </p>
                            </div>
                            <div className="divOuter">
                              <div className="divInner">
                                <input
                                  className="resend-code-input"
                                  type="text"
                                  maxLength={6}
                                  onChange={(e) => {
                                    this.setState({
                                      code: e.target.value,
                                    });
                                  }}
                                  // value={code}
                                />
                              </div>
                            </div>
                            <p
                              className="resend-code"
                              style={{ textAlign: "center" }}
                            >
                              Resend Code
                            </p>
                            <div className="continue-button">
                              <Button
                                color="primary"
                                className="w-md waves-effect waves-light"
                                type="submit"
                                onClick={this.confirmSignup}
                              >
                                Continue
                              </Button>
                            </div>
                          </div>
                        )}
                      </div>
                    </Col>
                  </Row>
                </div>
              </div>
            </Col>
            <Col lg={8}>
              <div className="authentication-bg">
                <div className="bg-overlay"></div>
              </div>
            </Col>
          </Row>
        </Container>
      </div>
    </React.Fragment>
  );
};

export default RegisterAdmin;
