import React, { useState, Fragment, useContext } from "react";

import Axios from "axios";

import {
  Form,
  Card,
  CardBody,
  Col,
  Row,
  Button,
  Container,
  Input,
  Label,
} from "reactstrap";

import { Editor } from "react-draft-wysiwyg";
import { EditorState } from "draft-js";
import ReadOnly from "./ReadOnly";
import EditableData from "./EditableData";

import { ArticlesContext } from "../ArticlesContext";

import "./DataTables.css";

const DataTable = () => {
  const { articles, addNew, columns } = useContext(ArticlesContext);
  const [articlesValue, setArticlesValue] = articles;
  const [addNewValue, setAddNewValue] = addNew;
  const [columnsValue, setcolumnsvalue] = columns;

  //USR DATA

  const [title, setTitle] = useState("");

  const [category, setCategory] = useState("");

  const [file, setFile] = useState([]);

  const [s3File, setS3File] = useState("");
  const [details, setDetails] = useState(EditorState.createEmpty());
  const [plainText, setPlainText] = useState("");

  const [searchParam, setSearchParam] = useState("");
  const [editContactId, setEditContactId] = useState(null);

  const [fieldsFilled, setFieldsFilled] = useState(false);

  const bodyParameters = {
    isFeatured: true,
    header: title,
    pic: s3File,
    detail: plainText,
    tag: ["Trending", "Most Recent", "Most Favorite", "Most Liked"],
    createdAt: "2021-11-16T13:56:51.163Z",
    author: 161,
    category: 1,
  };
  const checkFields = () => {
    if (
      title !== "" ||
      category !== "" ||
      category !== "Select a Category" ||
      details !== "" ||
      file !== undefined
    ) {
      setFieldsFilled(true);
    }
  };

  const editorStateChange = (details) => {
    setDetails(details);
    setPlainText(details.getCurrentContent().getPlainText());
  };

  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..qE3laVdfceQNTHjnh2ZyvR8kVlBe0bUSge0VNMdFGo9mYxdBZdNTRmRDDs5xJeZlO92yFjWZdm0hbHPu4Le5x_lV1G-1UN03QPEYspPPL7LGmZlrbY4SJW_shWiAI2OsnchdR6n3GOgP29v2ik6eNYZ63UmBJSfvbwNHwtYtGJ5wOb-aLoSKuVPcIVALog8FaOacUvWddDWCrktgNat78AxfyoW1TB_J5Q0FEMCLQYv8jp68eQYAQok2HIBQP0r99CxV-97N6Yt2oJSKOAOgpt_1Ob9AAag1SV3ueExg6mKGdXBNm4o2e5tijrog8vvcn0X0isaE3mcgXS68gjDSDA";

  const imgConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "multipart/form-data",
    },
  };

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const apiUrl = "http://18.216.174.90:3000/api/article";
  const imgUrl = "http://18.216.174.90:3000/api/user/upload";

  const fileSelectHandler = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);
  };
  console.log("Length: " + file);

  const createArticle = (e) => {
    e.preventDefault();
    console.log("Pressed the button!");
    const image = new FormData();
    checkFields();

    if (file.length !== undefined) {
      console.log("This ran");
      image.append("file", file, file.name);
      Axios.post(imgUrl, image, imgConfig)
        .then((response) =>
          setS3File(
            response.data.Location.replace(
              "https://petappfiles.s3.us-east-2.amazonaws.com/",
              ""
            )
          )
        )
        .catch((err) => console.log("error: " + err));
    }

    if (fieldsFilled) {
      Axios.post(apiUrl, bodyParameters, config)
        .then((response) => console.log(response))
        .catch((err) => console.log("error: " + err));
    }
  };

  const handleEditClick = (event, product) => {
    event.preventDefault();
    setEditContactId(product.id);
  };

  const filteredData = articlesValue.filter((place) =>
    place.name.toLowerCase().includes(searchParam.toLowerCase())
  );

  return (
    <div className="app-container">
      {!addNewValue ? (
        <div>
          <div className="table-top">
            <Input
              placeholder="Search the table by name..."
              style={{ width: "25%", borderRadius: "30px", height: "40px" }}
              onChange={(e) => setSearchParam(e.target.value)}
            />
            <Button
              type="button"
              color="success"
              className="btn-rounded mb-2 me-2"
              onClick={() => {
                setAddNewValue(true);
              }}
            >
              <i className="mdi mdi-plus me-1" /> Add Article
            </Button>
          </div>
          <div style={{ width: "100%", overflow: "auto" }}>
            <table style={{ fontSize: "5px" }}>
              <thead>
                {columnsValue.map((header) => (
                  <th> {header.title} </th>
                ))}
              </thead>
              <tbody>
                {filteredData.map((product) => (
                  <Fragment>
                    {editContactId === product.id ? (
                      <EditableData data={product} />
                    ) : (
                      <ReadOnly
                        data={product}
                        handleEditClick={handleEditClick}
                      />
                    )}
                  </Fragment>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="input-add-articles">
          <Container fluid>
            <Row>
              <Col>
                <Card>
                  <CardBody>
                    <Row className="mb-3">
                      <Col md={12}>
                        <Input
                          type="text"
                          placeholder="Add Title"
                          id="example-search-input"
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Col md={6}>
                        <select className="form-control">
                          <option defaultValue>Select a Category</option>
                          <option value="1">Article</option>
                          <option value="2">Blog</option>
                        </select>
                      </Col>
                      <Col md={6}>
                        <Input
                          type="text"
                          placeholder="Add Author"
                          id="example-search-input"
                          disabled
                        />
                      </Col>
                    </Row>

                    <Form method="post">
                      <Editor
                        editorState={details}
                        toolbarClassName="toolbarClassName"
                        wrapperClassName="wrapperClassName"
                        editorClassName="editorClassName"
                        onEditorStateChange={editorStateChange}
                      />
                    </Form>

                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                      }}
                    >
                      <div style={{ width: "67%" }}>
                        <Row className="mb-3">
                          <Label
                            htmlFor="example-search-input"
                            className="col-md-2 col-form-label"
                            style={{ marginTop: "15px" }}
                          >
                            Upload Images:
                          </Label>
                          <Col md={10}>
                            <div className="input-group">
                              <input
                                type="file"
                                className="form-control"
                                id="customFile"
                                onChange={fileSelectHandler}
                              />
                            </div>
                          </Col>
                        </Row>{" "}
                      </div>
                      <div style={{ marginTop: "20px" }}>
                        <div className="form-check mb-2">
                          <Input
                            type="checkbox"
                            className="form-check-input input-mini"
                            id="debugInfo"
                            value="checked"
                          />
                          <Label className="form-check-label" for="debugInfo">
                            Featured Blog
                          </Label>
                        </div>
                      </div>
                    </div>
                  </CardBody>
                  <div className="add-events-buttons">
                    <Button
                      color="light"
                      type="button"
                      className="waves-effect waves-light me-1"
                      outline
                      onClick={() => this.setState({ addNew: false })}
                    >
                      <i className="ri-close-line align-middle me-2"></i>
                      Cancel
                    </Button>

                    <Button
                      color="success"
                      type="button"
                      disabled
                      className="waves-effect waves-light me-1"
                      onClick={createArticle}
                    >
                      <i className="ri-add-circle-fill align-middle ms-2"></i>
                      Add Article
                    </Button>
                  </div>
                </Card>
              </Col>
            </Row>
          </Container>
        </div>
      )}
    </div>
  );
};

export default DataTable;
