import React, { useState } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";

const EditableData = ({ data, id, handleCancelClick }) => {
  const styles = {
    backgroundColor: "#EEEEEE",
    border: "none",
    height: "30px",
  };
  const errStyles = {
    borderColor: "red",
  };

  const [fieldsFilled, setFieldsFilled] = useState(true);

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");

  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [category, setCategory] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      url === "" ||
      details === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };
  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..deH7ObFUZ4HrRnbFYAARPmFeFXmMpWxzcWV7lrv6T2ZWuxt4UkKVjB2QOO5bCF5gVJHXTARLIj4rNYQNC3NMNZ6tJuU4q5qtEL65TVY1POiti-27xRKR_7HqizPsCNOjj7lByDZ_JI4nQalWSlE1Nr9SHSpuLz_eOx0xdveX9ywKuw1TCyPt28ETsIHR2utYthwXb9R65PgW7AdzSYWUMUMgTaNX8wqfVNZOIvHZKSbXHBOEzEqxB-OttzM96b7rb3ICDLCid9W_aS7HScfZN-4NQE1bFL1UZoXW7YDdM_h2owGZ9e9a7hKxTi9TcXgioYbppDeHhBGpsKVSxvznbA";

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const bodyParameters = {
    pic: "31b5fc4f-704a-43ce-92f9-933edf6b8effevent.jpeg",
    title: title,
    start_time: "2021-11-15T12:15:39.658Z",
    end_time: "2021-11-15T12:15:39.658Z",
    postal_code: postalCode,
    location: address,
    url: url,
    event_type: category,
    detail:
      "simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
    createdAt: "2021-11-15T12:15:39.658Z",
  };

  const editEvent = (e) => {
    Axios.post(apiUrl, bodyParameters, config)
      .then((response) => {
        console.log(response);
        window.location.reload(true);
      })
      .catch((err) => console.log("error: " + err));
  };

  const apiUrl = "http://18.216.174.90:3000/api/event";

  return (
    <tr>
      <td>
        <input
          required
          placeholder="Enter the title..."
          type="text"
          style={title === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setTitle(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the address..."
          type="text"
          style={address === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setTitle(e.target.value)}
        />
      </td>
      <td>
        <select
          className="form-control"
          onChange={(e) => setCategory(e.target.value)}
          style={
            (category === "" || category === "Select a Category") &&
            !fieldsFilled
              ? styles
              : null
          }
        >
          <option defaultValue>Select</option>
          <option value="Resturant">Online</option>
          <option value="Cafe">Offline</option>
        </select>
      </td>
      <td>
        <input
          required
          placeholder="Enter the Start time..."
          type="datetime-local"
          style={startTime === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setStartTime(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the End Time..."
          type="datetime-local"
          style={endTime === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setEndTime(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the postal code..."
          type="text"
          style={postalCode === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setPostalCode(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the URL..."
          type="text"
          style={url === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setUrl(e.target.value)}
        />
      </td>
      <td>
        <Link to="#" className="me-3 text-primary">
          <i class="ri-save-line"></i>
        </Link>
        <Link
          to="#"
          className="text-danger"
          onClick={(event) => handleCancelClick(event, data)}
        >
          <i class="ri-close-line"></i>
        </Link>
      </td>
    </tr>
  );
};

export default EditableData;
