import React, { useState, createContext } from "react";

export const ArticlesContext = createContext();

export const ArticlesProvider = (props) => {
  const [articles, setArticles] = useState([
    {
      id: 1,
      name: "Airi Satou",
      position: "Accountant",
      office: "Tokyo",
      age: "33",
    },

    {
      id: 2,
      name: "Angelica Ramos",
      position: "Chief Executive Officer (CEO)",
      office: "London",
      age: "47",
    },

    {
      id: 3,
      name: "Ashton Cox",
      position: "Junior Technical Author",
      office: "San Francisco",
      age: "66",
    },

    {
      id: 4,
      name: "Bradley Greer",
      position: "Software Engineer",
      office: "London",
      age: "41",
    },

    {
      id: 5,
      name: "Brenden Wagner",
      position: "Software Engineer",
      office: "San Francisco",
      age: "28",
    },
  ]);
  const [addNew, setAddNew] = useState(false);

  const [columns, setColumns] = useState([
    {
      title: "ID",
    },
    {
      title: "Title",
    },
    {
      title: "Category",
    },
    {
      title: "Author",
    },
    {
      title: "End Date",
    },
    {
      title: "Date",
    },
    {
      title: "Action",
    },
  ]);

  return (
    <ArticlesContext.Provider
      value={{
        places: [articles, setArticles],
        addNew: [addNew, setAddNew],
        columns: [columns, setColumns],
      }}
    >
      {props.children}
    </ArticlesContext.Provider>
  );
};
