import React, { useState, Fragment, useContext, useForm } from "react";
import { UncontrolledAlert } from "reactstrap";
import Axios from "axios";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";

import ReadOnly from "./ReadOnly";

import { EventsContext } from "../PetEventsContext";

import "./DataTables.css";

const DataTable = () => {
  //CONTEXT DATA
  const { events, state, columns } = useContext(EventsContext);
  const [eventsValue, setEventsValue] = events;
  const [stateValue, setStateValue] = state;
  const columnsValue = columns;

  //EVENT CREATIION DATA
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");
  const [category, setCategory] = useState("");
  const [file, setFile] = useState(null);
  const [s3File, setS3File] = useState("");

  //SEARCH & EDIT DATA
  const [searchParam, setSearchParam] = useState("");
  const [editEventData, setEditEventData] = useState([
    {
      id: "",
      title: "Hello",
      location: "",
      event: "",
      start: "",
      end: "",
      post: "",
      url: "",
      details: "",
    },
  ]);

  const [titleE, setTitleE] = useState(editEventData.title);
  const [addressE, setAddressE] = useState(editEventData.location);
  const [startTimeE, setStartTimeE] = useState(editEventData.start);
  const [endTimeE, setEndTimeE] = useState(editEventData.end);
  const [postalCodeE, setPostalCodeE] = useState(editEventData.post);
  const [urlE, setUrlE] = useState(editEventData.url);
  const [detailsE, setDetailsE] = useState(editEventData.details);
  const [categoryE, setCategoryE] = useState(editEventData.event);

  console.log(titleE);

  //FLAGS
  const [fieldsFilled, setFieldsFilled] = useState(null);
  const [deleteSuccess, setDeleteSuccess] = useState(null);

  //EXPORTED FUNCTIONS
  const deleteEventStatus = (e) => {
    setDeleteSuccess(e);
  };
  const getEditData = (e) => {
    setEditEventData({
      id: e.id,
      title: e.title,
      location: e.location,
      event: e.event_type,
      start: e.start_time,
      end: e.end_time,
      post: "",
      url: "",
      details: "",
    });
  };
  const setEditState = () => {
    setStateValue(2);
  };

  //CREATE EVENT DATA
  const createEventData = {
    pic: s3File,
    title: title,
    start_time: startTime,
    end_time: endTime,
    postal_code: postalCode,
    location: address,
    url: url,
    event_type: category,
    detail: details,
    createdAt: "2021-11-15T12:15:39.658Z",
  };

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      url === "" ||
      file === null ||
      details === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };
  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..Z_HQ_tmSWiJ405_rZbAineo1HAYUqTbqiE4-7M4Cm3LdE-UAZ7sKAW0uTW8yFi-h540l0GtVYLJgqPA1d0KEtbYy7PR7HU_sDSm64dr8IC_wXm0ZGGS__X7x8SxXLZy09dHIncq-qDKzepjwjVSXuPCzw409CDh-cS3bQqhV-qwdQXuadNahMLKnJvFJFaiZqhLkLqNiW59zBZmgX5LMkjZ1K2-vL7MJgt7KLtntOsEP7uTuE41aSBulj0PmKwXEHJlKtIA-bv5_FQXqxKWkqHwV50psGug5LjU5UV3SNiCg3Ew5cOdM77yNTbUU6mXJyWQxKqGhAsFTXhLOd1sJ1A";

  const apiUrl = "http://18.216.174.90:3000";
  const createUrl = `${apiUrl}/api/event`;
  const imgUrl = `${apiUrl}/api/user/upload`;
  const editUrl = `${apiUrl}/api/event/${editEventData[0].id}`;

  const imgConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "multipart/form-data",
    },
  };

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const fileSelectHandler = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);
  };

  const editEvent = (e) => {
    e.preventDefault();
    checkFields();
    const image = new FormData();
    image.append("file", file, file.name);

    Axios.post(imgUrl, image, imgConfig)
      .then((response) =>
        console.log(
          setS3File(
            response.data.Location.replace(
              "https://petappfiles.s3.us-east-2.amazonaws.com/",
              ""
            )
          )
        )
      )
      .catch((err) => console.log("error: " + err));
    if (fieldsFilled) {
      Axios.put(editUrl, createEventData, config)
        .then((response) => {
          window.location.reload(true);
        })
        .catch((err) => console.log("error: " + err));
    }
  };

  const createEvent = (e) => {
    e.preventDefault();
    checkFields();

    if (fieldsFilled) {
      const image = new FormData();
      image.append("file", file, file.name);

      Axios.post(imgUrl, image, imgConfig)
        .then((response) =>
          console.log(
            setS3File(
              response.data.Location.replace(
                "https://petappfiles.s3.us-east-2.amazonaws.com/",
                ""
              )
            )
          )
        )
        .catch((err) => console.log("error: " + err));
      console.log("Button pressed");
      Axios.post(createUrl, createEventData, config)
        .then((response) => {
          window.location.reload(true);
          console.log(response);
        })
        .catch((err) => console.log("error: " + err));
    }
  };

  const styles = {
    borderColor: "red",
  };

  const filteredData = eventsValue.filter((article) =>
    article.title.toLowerCase().includes(searchParam.toLowerCase())
  );

  return (
    <div className="app-container">
      {deleteSuccess === true ? (
        <UncontrolledAlert color="primary">
          Event deleted sucessfully
        </UncontrolledAlert>
      ) : null}
      {deleteSuccess === false ? (
        <UncontrolledAlert color="danger" role="alert">
          Could not delete event
        </UncontrolledAlert>
      ) : null}
      {stateValue === 0 ? (
        <div>
          <div className="table-top">
            <Input
              placeholder="Search the table by title..."
              style={{ width: "25%", borderRadius: "30px", height: "40px" }}
              onChange={(e) => setSearchParam(e.target.value)}
            />
            <Button
              type="button"
              color="success"
              className="btn-rounded mb-2 me-2"
              onClick={() => {
                setStateValue(1);
              }}
            >
              <i className="mdi mdi-plus me-1" /> Add Event
            </Button>
          </div>
          <div style={{ width: "100%", overflow: "auto" }}>
            <table style={{ fontSize: "5px" }}>
              <thead>
                {columnsValue.map((header) => (
                  <th> {header.title} </th>
                ))}
              </thead>
              <tbody>
                {filteredData.map((data) => (
                  <Fragment>
                    <ReadOnly
                      data={data}
                      id={data.id}
                      deleteEventStatus={deleteEventStatus}
                      getEditData={getEditData}
                      editState={setEditState}
                      token={token}
                    />
                  </Fragment>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : null}
      {stateValue === 1 ? (
        <div className="input-add-events">
          <Container fluid>
            <Row>
              <Col xs={12}>
                <Card>
                  <CardBody>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-text-input"
                        className="col-md-2 col-form-label"
                      >
                        Title*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            title === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Title"
                          }
                          id="title"
                          onChange={(e) => setTitle(e.target.value)}
                          style={
                            title === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Upload Images*
                      </Label>
                      <Col md={10}>
                        <div className="input-group">
                          <input
                            type="file"
                            className="form-control"
                            id="customFile"
                            onChange={fileSelectHandler}
                            style={
                              file === null && fieldsFilled === false
                                ? styles
                                : null
                            }
                          />
                        </div>
                      </Col>
                    </Row>

                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Address*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            address === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Address"
                          }
                          id="title"
                          onChange={(e) => setAddress(e.target.value)}
                          style={
                            address === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label className="col-md-2 col-form-label">
