import React, { useState, Fragment, useContext } from "react";
import Loading from "./loading.gif";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";

import ReadOnly from "./ReadOnly";

import { PlacesContext } from "../PlacesContext";

import Axios from "axios";

import "./DataTables.css";

const DataTable = () => {
  const { addNew, columns, placesData, state } = useContext(PlacesContext);

  const columnsValue = columns;
  const [stateValue, setStateValue] = state;
  const [placesDataValue, setPlacesDataValue] = placesData;

  //PLACES DATA

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [longituide, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [category, setCategory] = useState("");
  const [phone, setPhone] = useState("");
  const [fb, setFb] = useState("");
  const [insta, setInsta] = useState("");
  const [twitter, setTwitter] = useState("");
  const [url, setUrl] = useState("");
  const [email, setEmail] = useState("");
  const [file, setFile] = useState(null);
  const [s3File, setS3File] = useState("");

  // EDIT DATA

  const [titleE, setTitleE] = useState("");
  const [addressE, setAddressE] = useState("");
  const [longituideE, setLongitudeE] = useState("");
  const [latitudeE, setLatitudeE] = useState("");
  const [startTimeE, setStartTimeE] = useState("");
  const [endTimeE, setEndTimeE] = useState("");
  const [postalCodeE, setPostalCodeE] = useState("");
  const [categoryE, setCategoryE] = useState("");
  const [phoneE, setPhoneE] = useState("");
  const [fbE, setFbE] = useState("");
  const [instaE, setInstaE] = useState("");
  const [twitterE, setTwitterE] = useState("");
  const [urlE, setUrlE] = useState("");
  const [emailE, setEmailE] = useState("");

  //SEARCH AND
  const [searchParam, setSearchParam] = useState("");
  const [editContactId, setEditContactId] = useState(null);

  //FLAGS
  const [createFieldsFilled, setCreateFieldsFilled] = useState(null);

  const checkCreateFields = () => {
    if (
      title === "" ||
      address === "" ||
      longituide === "" ||
      latitude === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      phone === "" ||
      fb === "" ||
      insta === "" ||
      twitter === "" ||
      url === "" ||
      email === "" ||
      file === null
    ) {
      setCreateFieldsFilled(true);
    } else {
      setCreateFieldsFilled(false);
    }
  };

  const styles = {
    borderColor: "red",
  };

  const handleEditClick = (event, product) => {
    event.preventDefault();
    setEditContactId(product.id);
  };
  const handleCancelClick = (event, product) => {
    event.preventDefault();
    setEditContactId(null);
  };

  const filteredData = placesDataValue.filter((place) =>
    place.title.toLowerCase().includes(searchParam.toLowerCase())
  );

  const imgUrl = "http://18.216.174.90:3000/api/user/upload";

  const bodyParameters = {
    pic: {
      pic: [
        {
          url: s3File,
        },
      ],
    },
    title: title,
    title_icon: "760634d2-f8ec-4caa-9a2d-14a8828cfb5dpetplace.jpg",
    social_link: {
      social_link: [
        {
          facebook: fb,
        },
        {
          instagram: insta,
        },
      ],
    },
    phone_number: phone,
    address: address,
    lat: latitude,
    long: longituide,
    postcode: postalCode,
    start_time: startTime,
    end_time: endTime,
    email: email,
    web_url: url,
    createdAt: "2021-11-15T12:15:39.658Z",
    category: 1,
  };

  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..p1nfxW1aML9HDVctKuWqRZAgcpmexw3oV042xy0jhGIUswwkto4cdOf6CskMNgv68HJS--XW1aE4l5ev2TrqMVPPRuO5dESrYsWZKiyBkm25hU8SUPtgZcN_mo2Dl8CDYK68o0aZBrWt5_k9cCMr3oAFK9XSXX50LgGtNKjY_LtHl_g5hmlf-TfA1HiFjo8nvHSmkWeDZGFIdsaXRq6lMicx3VaLOlYMzMJ42i7LSgEMmsF0fxXiZOkXnaUSM9wfys1o36RcfRb857TEZBC462XxfK-CknjtXp68MKFURpJGoH0jJ0XRMHZnqAvM2hzr-wfud1VBmzJ8nzgnBtFTOg";

  const imgConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "multipart/form-data",
    },
  };

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const apiUrl = "http://18.216.174.90:3000/api/petplace";

  const fileSelectHandler = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);
    const image = new FormData();
    if (file !== null) {
      image.append("file", file, file.name);
      console.log(image);
      Axios.post(imgUrl, image, imgConfig)
        .then((response) => {
          console.log(response);
          setS3File(response.data.key);
        })
        .catch((err) => console.log("error: " + err));
    }
  };

  const createPlace = (e) => {
    e.preventDefault();
    checkCreateFields();

    Axios.post(apiUrl, bodyParameters, config)
      .then((response) => {
        window.location.reload(true);
      })
      .catch((err) => console.log("error: " + err));
  };

  return (
    <div className="app-container">
      <div>
        <div className="table-top">
          <Input
            placeholder="Search the table by title..."
            style={{ width: "25%", borderRadius: "30px", height: "40px" }}
            onChange={(e) => setSearchParam(e.target.value)}
          />
          <Button
            type="button"
            color="success"
            className="btn-rounded mb-2 me-2"
            onClick={() => {
              setStateValue(1);
            }}
          >
            <i className="mdi mdi-plus me-1" /> Add Place
          </Button>
        </div>
        <div style={{ width: "100%", overflow: "auto" }}>
          {placesDataValue.length > 0 ? (
            <table style={{ fontSize: "5px" }}>
              <thead>
                {columnsValue.map((header) => (
                  <th> {header.title} </th>
                ))}
              </thead>
              <tbody>
                {filteredData.map((data) => (
                  <ReadOnly data={data} id={data.id} token={token} />
                ))}
              </tbody>
            </table>
          ) : (
            <img
              src={Loading}
              alt="loading"
              style={{
                display: "block",
                marginLeft: "auto",
                marginRight: "auto",
                width: "10%",
              }}
            />
          )}
        </div>
      </div>
      <div className="input-add-events">
        <Container fluid>
          <Row>
            <Col xs={12}>
              <Card>
                <CardBody>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-text-input"
                      className="col-md-2 col-form-label"
                    >
                      Title*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="text"
                        placeholder={
                          title === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Title"
                        }
                        id="title"
                        onChange={(e) => setTitle(e.target.value)}
                        style={
                          title === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-search-input"
                      className="col-md-2 col-form-label"
                    >
                      Address*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="text"
                        placeholder={
                          address === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Address"
                        }
                        id="title"
                        onChange={(e) => setAddress(e.target.value)}
                        style={
                          address === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-search-input"
                      className="col-md-2 col-form-label"
                    >
                      Longitude & Latitude*
                    </Label>
                    <Col md={5}>
                      <Input
                        type="text"
                        placeholder={
                          longituide === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Longitude"
                        }
                        id="example-search-input"
                        onChange={(e) => setLongitude(e.target.value)}
                        style={
                          longituide === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                    <Col md={5}>
                      <Input
                        type="text"
                        placeholder={
                          latitude === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Latitude"
                        }
                        id="example-search-input"
                        onChange={(e) => setLatitude(e.target.value)}
                        style={
                          latitude === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-time-input"
                      className="col-md-2 col-form-label"
                    >
                      Open Time*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="datetime-local"
                        placeholder={
                          startTime === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Start Time"
                        }
                        id="example-time-input"
                        onChange={(e) => setStartTime(e.target.value)}
                        style={
                          startTime === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-time-input"
                      className="col-md-2 col-form-label"
                    >
                      Close Time*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="datetime-local"
                        placeholder={
                          endTime === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter End Time"
                        }
                        id="example-time-input"
                        onChange={(e) => setEndTime(e.target.value.toString())}
                        style={
                          endTime === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-search-input"
                      className="col-md-2 col-form-label"
                    >
                      Postal Code*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="text"
                        placeholder={
                          postalCode === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Postal Code"
                        }
                        id="example-search-input"
                        onChange={(e) => setPostalCode(e.target.value)}
                        style={
                          postalCode === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>

                  <Row className="mb-3">
                    <Label className="col-md-2 col-form-label">Category*</Label>
                    <Col md={10}>
                      <select
                        className="form-control"
                        onChange={(e) => setCategory(e.target.value)}
                        style={
                          (category === "" ||
                            category === "Select a Category") &&
                          createFieldsFilled === false
                            ? styles
                            : null
                        }
                      >
                        <option defaultValue>Select a Category</option>
                        <option value="Resturant">Resturant</option>
                        <option value="Cafe">Cafe</option>
                        <option value="Pub">Pub</option>
                        <option value="Park">Park</option>
                        <option value="Hotel">Hotel</option>
                      </select>
                    </Col>
                  </Row>

                  <Row className="mb-3">
                    <Label
                      htmlFor="example-tel-input"
                      className="col-md-2 col-form-label"
                    >
                      Phone*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="tel"
                        placeholder={
                          phone === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Phone"
                        }
                        id="example-tel-input"
                        onChange={(e) => setPhone(e.target.value)}
                        style={
                          phone === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-url-input"
                      className="col-md-2 col-form-label"
                    >
                      Facebook Link*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="text"
                        placeholder={
                          fb === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Facebook Link"
                        }
                        id="example-url-input"
                        onChange={(e) => setFb(e.target.value)}
                        style={
                          phone === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-url-input"
                      className="col-md-2 col-form-label"
                    >
                      Instagram Link*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="text"
                        placeholder={
                          insta === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Instagram Link"
                        }
                        id="example-url-input"
                        onChange={(e) => setInsta(e.target.value)}
                        style={
                          insta === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-url-input"
                      className="col-md-2 col-form-label"
                    >
                      Twitter Link*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="text"
                        placeholder={
                          twitter === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Twitter Link"
                        }
                        id="example-url-input"
                        onChange={(e) => setTwitter(e.target.value)}
                        style={
                          twitter === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-url-input"
                      className="col-md-2 col-form-label"
                    >
                      Email*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="text"
                        placeholder={
                          email === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter Email"
                        }
                        id="example-url-input"
                        onChange={(e) => setEmail(e.target.value)}
                        style={
                          email === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-url-input"
                      className="col-md-2 col-form-label"
                    >
                      URL Web*
                    </Label>
                    <Col md={10}>
                      <Input
                        type="text"
                        placeholder={
                          url === "" && createFieldsFilled === true
                            ? "This is a required field"
                            : "Enter URL"
                        }
                        id="example-url-input"
                        onChange={(e) => setUrl(e.target.value)}
                        style={
                          url === "" && createFieldsFilled === true
                            ? styles
                            : null
                        }
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Label
                      htmlFor="example-search-input"
                      className="col-md-2 col-form-label"
                    >
                      Upload Images*
                    </Label>
                    <Col md={10}>
                      <div className="input-group">
                        <input
                          type="file"
                          className="form-control"
                          id="customFile"
                          onChange={fileSelectHandler}
                          style={
                            file === null && createFieldsFilled === true
                              ? styles
                              : null
                          }
                        />
                      </div>
                    </Col>
                  </Row>
                  {createFieldsFilled ? (
                    <p style={{ color: "red", textAlign: "center" }}>
                      One or more required fields are missing{" "}
                    </p>
                  ) : null}
                </CardBody>
                <div className="add-events-buttons">
                  <Button
                    color="light"
                    type="button"
                    className="waves-effect waves-light me-1"
                    outline
                    onClick={() => {
                      setCreateFieldsFilled(null);
                      setAddNewValue(false);
                    }}
                  >
                    <i className="ri-close-line align-middle me-2"></i> Cancel
                  </Button>

                  <Button
                    color="success"
                    type="button"
                    className="waves-effect waves-light me-1"
                    onClick={createPlace}
                  >
                    <i className="ri-add-circle-fill align-middle ms-2"></i>
                    Add Place
                  </Button>
                </div>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
      )}
    </div>
  );
};

export default DataTable;
