import React, { useState } from "react";
import { Link } from "react-router-dom";

const EditableData = ({ data, id, handleCancelClick }) => {
  const styles = {
    backgroundColor: "#EEEEEE",
    border: "none",
    height: "30px",
  };
  const errStyles = {
    borderColor: "red",
  };

  const [fieldsFilled, setFieldsFilled] = useState(true);

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");

  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [category, setCategory] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      url === "" ||
      details === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };

  return (
    <tr>
      <td>
        <input
          required
          placeholder="Enter the title..."
          type="text"
          style={title === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setTitle(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the address..."
          type="text"
          style={address === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setTitle(e.target.value)}
        />
      </td>
      <td>
        <select
          className="form-control"
          onChange={(e) => setCategory(e.target.value)}
          style={
            (category === "" || category === "Select a Category") &&
            !fieldsFilled
              ? styles
              : null
          }
        >
          <option defaultValue>Select</option>
          <option value="Resturant">Online</option>
          <option value="Cafe">Offline</option>
        </select>
      </td>
      <td>
        <input
          required
          placeholder="Enter the Start time..."
          type="datetime-local"
          style={startTime === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setStartTime(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the End Time..."
          type="datetime-local"
          style={endTime === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setEndTime(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the postal code..."
          type="text"
          style={postalCode === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setPostalCode(e.target.value)}
        />
      </td>
      <td>
        <input
          required
          placeholder="Enter the URL..."
          type="text"
          style={url === "" && !fieldsFilled ? errStyles : styles}
          onClick={(e) => setUrl(e.target.value)}
        />
      </td>
      <td>
        <Link to="#" className="me-3 text-primary">
          <i class="ri-save-line"></i>
        </Link>
        <Link
          to="#"
          className="text-danger"
          onClick={(event) => handleCancelClick(event, data)}
        >
          <i class="ri-close-line"></i>
        </Link>
      </td>
    </tr>
  );
};

export default EditableData;
