import React, { useState, Fragment, useContext } from "react";

import Axios from "axios";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";

import ReadOnly from "./ReadOnly";
import EditableData from "./EditableData";

import { EventsContext } from "../PetEventsContext";

import "./DataTables.css";

const DataTable = () => {
  const { places, addNew, columns } = useContext(EventsContext);
  const [placesValue, setPlacesValue] = places;
  const [addNewValue, setAddNewValue] = addNew;
  const [columnsValue, setcolumnsvalue] = columns;

  //USR DATA

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");
  const [category, setCategory] = useState("");

  const [file, setFile] = useState([]);
  const [s3File, setS3File] = useState("");

  const [searchParam, setSearchParam] = useState("");
  const [editContactId, setEditContactId] = useState(null);

  const [fieldsFilled, setFieldsFilled] = useState(null);

  const bodyParameters = {
    pic: s3File,
    title: title,
    start_time: "2021-11-15T12:15:39.658Z",
    end_time: "2021-11-15T12:15:39.658Z",
    postal_code: postalCode,
    location: address,
    url: url,
