import React, { useState, Fragment, useContext } from "react";

import Axios from "axios";

import {
  Form,
  Card,
  CardBody,
  Col,
  Row,
  Button,
  Container,
  Input,
  Label,
} from "reactstrap";

import { Editor } from "react-draft-wysiwyg";
import ReadOnly from "./ReadOnly";
import EditableData from "./EditableData";

import { ArticlesContext } from "../ArticlesContext";

import "./DataTables.css";

const DataTable = () => {
  const { articles, addNew, columns } = useContext(ArticlesContext);
  const [articlesValue, setArticlesValue] = articles;
  const [addNewValue, setAddNewValue] = addNew;
  const [columnsValue, setcolumnsvalue] = columns;

  //USR DATA

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");
  const [category, setCategory] = useState("");

  const [file, setFile] = useState([]);
  const [s3File, setS3File] = useState("");

  const [searchParam, setSearchParam] = useState("");
  const [editContactId, setEditContactId] = useState(null);

  const [fieldsFilled, setFieldsFilled] = useState(null);

  const bodyParameters = {
    pic: s3File,
    title: title,
    start_time: "2021-11-15T12:15:39.658Z",
    end_time: "2021-11-15T12:15:39.658Z",
    postal_code: postalCode,
    location: address,
    url: url,
    event_type: category,
    detail: details,
    createdAt: "2021-11-15T12:15:39.658Z",
  };

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      url === "" ||
      details === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };

  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..PYEFqVQhQCckC7XMxmo3Hgy1r9oPmrTPMZKlhiMG3lh4Px5jJsZSFN_TQLHRMdoDwBTwGB7D2jpRVbDAAIEx1k9kPcy-u9e9wdSHhYF09wNTna4CMbY1397uZ978vMTaPCs4jiMsN4-kYGxXon8LXP2N09qrESpTdvztBGC0EkwbEYiOjhW2EH0RQVV6LMhzrKmWOeH7Y8SKlz3PKl-ZBgrlKmTtKeAiVzMmZkjmyH2ttryOlYHMMyuu_8an2NcXdiG3qo_D2cHlOAMDl5y83R91m0v5i5rExmMX0hNaS3ksaqs6EbvEzCMgaE4qRQ4nYrbGxK36OLUQ9mK4gYSkqg";

  const imgConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "multipart/form-data",
    },
  };

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const apiUrl = "http://18.216.174.90:3000/api/event";
  const imgUrl = "http://18.216.174.90:3000/api/user/upload";

  const fileSelectHandler = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);
  };

  const createEvent = (e) => {
    e.preventDefault();
    console.log("Pressed the button!");
    const image = new FormData();
    image.append("file", file, file.name);

    Axios.post(imgUrl, image, imgConfig)
      .then((response) =>
        console.log(
          setS3File(
            response.data.Location.replace(
              "https://petappfiles.s3.us-east-2.amazonaws.com/",
              ""
            )
          )
        )
      )
      .catch((err) => console.log("error: " + err));

    console.log(s3File);

    Axios.post(apiUrl, bodyParameters, config)
      .then((response) => console.log(response))
      .catch((err) => console.log("error: " + err));
  };

  const styles = {
    borderColor: "red",
  };

  const handleEditClick = (event, product) => {
    event.preventDefault();
    setEditContactId(product.id);
  };

  const filteredData = articlesValue.filter((place) =>
    place.name.toLowerCase().includes(searchParam.toLowerCase())
  );

  return (
    <div className="app-container">
      {!addNewValue ? (
        <div>
          <div className="table-top">
            <Input
              placeholder="Search the table by name..."
              style={{ width: "25%", borderRadius: "30px", height: "40px" }}
              onChange={(e) => setSearchParam(e.target.value)}
            />
            <Button
              type="button"
              color="success"
              className="btn-rounded mb-2 me-2"
              onClick={() => {
                setAddNewValue(true);
              }}
            >
              <i className="mdi mdi-plus me-1" /> Add Article
            </Button>
          </div>
          <div style={{ width: "100%", overflow: "auto" }}>
            <table style={{ fontSize: "5px" }}>
              <thead>
                {columnsValue.map((header) => (
                  <th> {header.title} </th>
                ))}
              </thead>
              <tbody>
                {filteredData.map((product) => (
                  <Fragment>
                    {editContactId === product.id ? (
                      <EditableData data={product} />
                    ) : (
                      <ReadOnly
                        data={product}
                        handleEditClick={handleEditClick}
                      />
                    )}
                  </Fragment>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="input-add-articles">
          <Container fluid>
            <Row>
              <Col>
                <Card>
                  <CardBody>
                    <Row className="mb-3">
                      <Col md={12}>
                        <Input
                          type="text"
                          placeholder="Add Title"
                          id="example-search-input"
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Col md={6}>
                        <select className="form-control">
                          <option defaultValue>Select a Category</option>
                          <option value="1">Article</option>
                          <option value="2">Blog</option>
                        </select>
                      </Col>
                      <Col md={6}>
                        <Input
                          type="text"
                          placeholder="Add Author"
                          id="example-search-input"
                          disabled
                        />
                      </Col>
                    </Row>

                    <Form method="post">
                      <Editor
                        toolbarClassName="toolbarClassName"
                        wrapperClassName="wrapperClassName"
                        editorClassName="editorClassName"
                      />
                    </Form>
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                      }}
                    >
                      <div style={{ width: "67%" }}>
                        <Row className="mb-3">
                          <Label
                            htmlFor="example-search-input"
                            className="col-md-2 col-form-label"
                            style={{ marginTop: "15px" }}
                          >
                            Upload Images:
                          </Label>
                          <Col md={10}>
                            <div className="input-group">
                              <input
                                type="file"
                                className="form-control"
                                id="customFile"
                                onChange={fileSelectHandler}
                              />
                            </div>
                          </Col>
                        </Row>{" "}
                      </div>
                      <div style={{ marginTop: "20px" }}>
                        <div className="form-check mb-2">
                          <Input
                            type="checkbox"
                            className="form-check-input input-mini"
                            id="debugInfo"
                            value="checked"
                          />
                          <Label className="form-check-label" for="debugInfo">
                            Featured Blog
                          </Label>
                        </div>
                      </div>
                    </div>
                  </CardBody>
                  <div className="add-events-buttons">
                    <Button
                      color="light"
                      type="button"
                      className="waves-effect waves-light me-1"
                      outline
                      onClick={() => this.setState({ addNew: false })}
                    >
                      <i className="ri-close-line align-middle me-2"></i>
                      Cancel
                    </Button>

                    <Button
                      color="success"
                      type="button"
                      className="waves-effect waves-light me-1"
                    >
                      <i className="ri-add-circle-fill align-middle ms-2"></i>
                      Add Article
                    </Button>
                  </div>
                </Card>
              </Col>
            </Row>
          </Container>
        </div>
      )}
    </div>
  );
};

export default DataTable;
