import React, { Component } from "react";
import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";
import { Link } from "react-router-dom";
// import Flatpickr from "react-flatpickr";
import products from "./products.json";
// datatable related plugins
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
  PaginationListStandalone,
  SizePerPageDropdownStandalone,
} from "react-bootstrap-table2-paginator";

import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import "./datatables.scss";
import "./PetEvents.scss";

// Table data

class PetEvents extends Component {
  constructor(props) {
    super(props);
    this.state = {
      breadcrumbItems: [
        { title: "Tables", link: "#" },
        { title: "Data Table", link: "#" },
      ],
      page: 1,
      sizePerPage: 10,
      productData: products,
      addNew: false,
      eventID: "",
      title: "",
      startDate: "",
      endDate: "",
      time: "",
      address: "",
      link: "",
      details: "",
      uploadedImage: null,
    };

    this.handleStart = this.handleStart.bind(this);
    this.handleEnd = this.handleEnd.bind(this);
  }
  componentDidMount() {
    try {
      const products = JSON.parse(localStorage.getItem("products"));
    } catch (e) {}
  }

  handleDefault(date) {
    this.setState({ default_date: date });
  }
  handleStart = (date) => {
    this.setState({ start_date: date });
  };
  handleEnd = (date) => {
    this.setState({ end_date: date });
  };
  handleMonthChange(date) {
    this.setState({ monthDate: date });
  }
  handleYearChange(date) {
    this.setState({ yearDate: date });
  }
  handleFileSelect = (event) => {
    this.setState(this.setState({ uploadedImage: event.target.files[0] }));
  };

  render() {
    const columns = [
      {
        dataField: "eventID",
        text: "Event ID",
        sort: true,
      },
      {
        dataField: "title",
        text: "Title",
        sort: false,
      },
      {
        dataField: "image",
        text: "Image",
        sort: false,
      },
      {
        dataField: "startDate",
        text: "Start Date",
        sort: true,
      },
      {
        dataField: "endDate",
        text: "End Date",
        sort: true,
      },
      {
        dataField: "time",
        text: "Time",
        sort: true,
      },
      {
        dataField: "address",
        text: "Address",
        sort: true,
      },
      {
        dataField: "details",
        text: "Details",
        sort: true,
      },
      {
        dataField: "link",
        text: "Link",
        sort: true,
      },

      {
        dataField: "menu",
        isDummyField: true,
        text: "Action",
        formatter: () => (
          <>
            <Link to="#" className="me-3 text-primary">
              <a
                href="/#"
                onClick={() => {
                  console.log(this.state.eventID);
                }}
              >
                <i className="mdi mdi-pencil font-size-18"></i>
              </a>
            </Link>
            <Link to="#" className="text-danger">
              <i className="mdi mdi-trash-can font-size-18"></i>
            </Link>
          </>
        ),
      },
    ];

    const defaultSorted = [
      {
        dataField: "id",
        order: "asc",
      },
    ];

    const pageOptions = {
      sizePerPage: 10,
      totalSize: products.length, // replace later with size(customers),
      custom: true,
    };

    // Select All Button operation
    const selectRow = {
      mode: "checkbox",
    };

    const { SearchBar } = Search;

    return (
      <React.Fragment>
        <div className="page-content">
          {!this.state.addNew ? (
            <div className="container-fluid">
              <Breadcrumbs
                title="Pet Events"
                breadcrumbItems={this.state.breadcrumbItems}
              />

              <Row>
                <Col className="col-12">
                  <Card>
                    <CardBody>
                      <PaginationProvider
                        pagination={paginationFactory(pageOptions)}
                        keyField="id"
                        columns={columns}
                        data={this.state.productData}
                      >
                        {({ paginationProps, paginationTableProps }) => (
                          <ToolkitProvider
                            keyField="id"
                            columns={columns}
                            data={this.state.productData}
                            search
                          >
                            {(toolkitProps) => (
                              <React.Fragment>
                                <Row className="mb-2">
                                  <Col md="4">
                                    <div className="search-box me-2 mb-2 d-inline-block">
                                      <div className="position-relative">
                                        <SearchBar
                                          {...toolkitProps.searchProps}
                                        />
                                        <i className="search-box chat-search-box" />
                                      </div>
                                    </div>
                                  </Col>
                                  <Col sm="8">
                                    <div className="text-sm-end">
                                      <Button
                                        type="button"
                                        color="success"
                                        className="btn-rounded mb-2 me-2"
                                        onClick={() =>
                                          this.setState({ addNew: true })
                                        }
                                      >
                                        <i className="mdi mdi-plus me-1" /> Add
                                        Event
                                      </Button>
                                    </div>
                                  </Col>
                                </Row>

                                <Row>
                                  <Col xl="12">
                                    <div className="table-responsive">
                                      <BootstrapTable
                                        keyField={"id"}
                                        responsive
                                        bordered={false}
                                        striped={false}
                                        defaultSorted={defaultSorted}
                                        selectRow={selectRow}
                                        classes={
                                          "table align-middle table-nowrap"
                                        }
                                        headerWrapperClasses={"thead-light"}
                                        {...toolkitProps.baseProps}
                                        {...paginationTableProps}
                                      />
                                    </div>
                                  </Col>
                                </Row>

                                <Row className="align-items-md-center mt-30">
                                  <Col className="inner-custom-pagination d-flex">
                                    <div className="d-inline">
                                      <SizePerPageDropdownStandalone
                                        {...paginationProps}
                                      />
                                    </div>
                                    <div className="text-md-right ms-auto">
                                      <PaginationListStandalone
                                        {...paginationProps}
                                      />
                                    </div>
                                  </Col>
                                </Row>
                              </React.Fragment>
                            )}
                          </ToolkitProvider>
                        )}
                      </PaginationProvider>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </div>
          ) : (
            <div className="input-add-events">
              <Container fluid>
                <Breadcrumbs
                  title="Add an Event"
                  breadcrumbItems={this.state.breadcrumbItems}
                />

                <Row>
                  <Col xs={12}>
                    <Card>
                      <CardBody>
                        <Row className="mb-3">
                          <Label
                            htmlFor="example-text-input"
                            className="col-md-2 col-form-label"
                          >
                            Title
                          </Label>
                          <Col md={10}>
                            <Input
                              type="text"
                              placeholder="Add the Title"
                              id="example-text-input"
                              onChange={(e) => {
                                this.setState({ title: e.target.value });
                              }}
                            />
                          </Col>
                        </Row>

                        <Row className="mb-3">
                          <Label
                            htmlFor="example-search-input"
                            className="col-md-2 col-form-label"
                          >
                            Upload Image
                          </Label>
                          <Col md={10}>
                            <div className="input-group">
                              <input
                                type="file"
                                className="form-control"
                                id="customFile"
                                onChange={this.handleFileSelect}
                              />
                            </div>
                          </Col>
                        </Row>

                        <Row className="mb-3">
                          <Label
                            htmlFor="example-date-input"
                            className="col-md-2 col-form-label"
                          >
                            Start Date
                          </Label>
                          <Col md={10}>
                            <Input
                              type="date"
                              placeholder="Add Start Date"
                              id="example-date-input"
                              onChange={(e) => {
                                this.setState({ startDate: e.target.value });
                              }}
                            />
                          </Col>
                        </Row>
                        <Row className="mb-3">
                          <Label
                            htmlFor="example-date-input"
                            className="col-md-2 col-form-label"
                          >
                            End Date
                          </Label>
                          <Col md={10}>
                            <Input
                              type="date"
                              placeholder="Add End Date"
                              id="example-date-input"
                              onChange={(e) => {
                                this.setState({ endDate: e.target.value });
                              }}
                            />
                          </Col>
                        </Row>

                        <Row className="mb-3">
                          <Label
                            htmlFor="example-time-input"
                            className="col-md-2 col-form-label"
                          >
                            Time
                          </Label>
                          <Col md={10}>
                            <Input
                              type="time"
                              defaultValue="13:45:00"
                              id="example-time-input"
                              onChange={(e) => {
                                this.setState({ time: e.target.value });
                              }}
                            />
                          </Col>
                        </Row>
                        <Row className="mb-3">
                          <Label
                            htmlFor="example-text-input"
                            className="col-md-2 col-form-label"
                          >
                            Address
                          </Label>
                          <Col md={10}>
                            <Input
                              type="text"
                              placeholder="Add the Address"
                              id="example-text-input"
                              onChange={(e) => {
                                this.setState({ address: e.target.value });
                              }}
                            />
                          </Col>
                        </Row>
                        <Row className="mb-3">
                          <Label
                            htmlFor="example-text-input"
                            className="col-md-2 col-form-label"
                          >
                            URL
                          </Label>
                          <Col md={10}>
                            <Input
                              type="text"
                              placeholder="Add the URL"
                              id="example-text-input"
                              onChange={(e) => {
                                this.setState({ URL: e.target.value });
                              }}
                            />
                          </Col>
                        </Row>

                        <Row className="mb-3">
                          <Label
                            htmlFor="example-text-input"
                            className="col-md-2 col-form-label"
                          >
                            Event Details
                          </Label>
                          <Col md={10}>
                            <Input
                              type="textarea"
                              placeholder="Add Event Details"
                              id="example-text-input"
                              onChange={(e) => {
                                this.setState({ details: e.target.value });
                              }}
                            />
                          </Col>
                        </Row>
                      </CardBody>
                      <div className="add-events-buttons">
                        <Button
                          color="light"
                          type="button"
                          className="waves-effect waves-light me-1"
                          outline
                          onClick={() => this.setState({ addNew: false })}
                        >
                          <i className="ri-close-line align-middle me-2"></i>{" "}
                          Cancel
                        </Button>
                        <Button
                          color="success"
                          type="button"
                          className="waves-effect waves-light me-1"
                          onClick={() => {
                            products.push({
                              eventID: products.length + 1,
                              title: this.state.title,
                              startDate: this.state.startDate,
                              endDate: this.state.endDate,
                              time: this.state.time,
                              address: this.state.address,
                              link: this.state.URL,
                              details: this.state.details,
                              uploadedImage: "Image not available",
                            });
                            localStorage.setItem(
                              "products",
                              JSON.stringify(products)
                            );
                            this.setState({ addNew: false });
                          }}
                        >
                          <i className="ri-add-circle-fill align-middle ms-2"></i>
                          Add Event
                        </Button>
                      </div>
                    </Card>
                  </Col>
                </Row>
              </Container>
            </div>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default PetEvents;
