import React, { useState, Fragment, useContext } from "react";
import { UncontrolledAlert } from "reactstrap";
import Axios from "axios";
import { Link } from "react-router-dom";

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  Container,
} from "reactstrap";
import ReadOnly from "./ReadOnly";

import { EventsContext } from "../PetEventsContext";

import "./DataTables.css";

const DataTable = () => {
  const { events, state, columns } = useContext(EventsContext);
  const [eventsValue, setEventsValue] = events;
  const [stateValue, setStateValue] = state;
  const columnsValue = columns;

  //USR DATA

  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [url, setUrl] = useState("");
  const [details, setDetails] = useState("");
  const [category, setCategory] = useState("");

  const [file, setFile] = useState([]);
  const [s3File, setS3File] = useState("");

  const [searchParam, setSearchParam] = useState("");
  const [editEventData, setEditEventData] = useState([{}]);

  const [fieldsFilled, setFieldsFilled] = useState(null);
  const [deleteSuccess, setDeleteSuccess] = useState(null);

  const bodyParameters = {
    pic: s3File,
    title: title,
    start_time: "2021-11-15T12:15:39.658Z",
    end_time: "2021-11-15T12:15:39.658Z",
    postal_code: postalCode,
    location: address,
    url: url,
    event_type: category,
    detail: details,
    createdAt: "2021-11-15T12:15:39.658Z",
  };

  const checkFields = () => {
    if (
      title === "" ||
      address === "" ||
      startTime === "" ||
      endTime === "" ||
      postalCode === "" ||
      category === "" ||
      category === "Select a Category" ||
      url === "" ||
      details === ""
    ) {
      setFieldsFilled(false);
    } else {
      setFieldsFilled(true);
    }
  };

  const token =
    "eyJraWQiOiJqYUh6OTFhVytuUSt4S1Jnd0Rjd1pUV0ZsbW1YUTBtZ0V0ZXVsUFFpTmtZPSIsImFsZyI6IlJTMjU2In0..deH7ObFUZ4HrRnbFYAARPmFeFXmMpWxzcWV7lrv6T2ZWuxt4UkKVjB2QOO5bCF5gVJHXTARLIj4rNYQNC3NMNZ6tJuU4q5qtEL65TVY1POiti-27xRKR_7HqizPsCNOjj7lByDZ_JI4nQalWSlE1Nr9SHSpuLz_eOx0xdveX9ywKuw1TCyPt28ETsIHR2utYthwXb9R65PgW7AdzSYWUMUMgTaNX8wqfVNZOIvHZKSbXHBOEzEqxB-OttzM96b7rb3ICDLCid9W_aS7HScfZN-4NQE1bFL1UZoXW7YDdM_h2owGZ9e9a7hKxTi9TcXgioYbppDeHhBGpsKVSxvznbA";

  const imgConfig = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "multipart/form-data",
    },
  };

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  const apiUrl = "http://18.216.174.90:3000/api/event";
  const imgUrl = "http://18.216.174.90:3000/api/user/upload";

  const fileSelectHandler = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);
  };

  const createEvent = (e) => {
    e.preventDefault();
    checkFields();
    const image = new FormData();
    image.append("file", file, file.name);

    Axios.post(imgUrl, image, imgConfig)
      .then((response) =>
        console.log(
          setS3File(
            response.data.Location.replace(
              "https://petappfiles.s3.us-east-2.amazonaws.com/",
              ""
            )
          )
        )
      )
      .catch((err) => console.log("error: " + err));
    if (fieldsFilled) {
      Axios.post(apiUrl, bodyParameters, config)
        .then((response) => {
          window.location.reload(true);
        })
        .catch((err) => console.log("error: " + err));
    }
  };

  const styles = {
    borderColor: "red",
  };

  const filteredData = eventsValue.filter((article) =>
    article.title.toLowerCase().includes(searchParam.toLowerCase())
  );

  return (
    <div className="app-container">
      {deleteSuccess === true ? (
        <UncontrolledAlert color="primary">
          Event deleted sucessfully
        </UncontrolledAlert>
      ) : null}
      {deleteSuccess === false ? (
        <UncontrolledAlert color="danger" role="alert">
          Could not delete event
        </UncontrolledAlert>
      ) : null}
      {stateValue === 0 ? (
        <div>
          <div className="table-top">
            <Input
              placeholder="Search the table by title..."
              style={{ width: "25%", borderRadius: "30px", height: "40px" }}
              onChange={(e) => setSearchParam(e.target.value)}
            />
            <Button
              type="button"
              color="success"
              className="btn-rounded mb-2 me-2"
              onClick={() => {
                setStateValue(1);
              }}
            >
              <i className="mdi mdi-plus me-1" /> Add Event
            </Button>
          </div>
          <div style={{ width: "100%", overflow: "auto" }}>
            <table style={{ fontSize: "5px" }}>
              <thead>
                {columnsValue.map((header) => (
                  <th> {header.title} </th>
                ))}
              </thead>
              <tbody>
                {filteredData.map((data) => (
                  <tr>
                    <td>{data.title}</td>
                    <td>{data.location}</td>
                    <td>
                      {data.event_type.charAt(0).toUpperCase() +
                        data.event_type.slice(1)}
                    </td>
                    <td>{data.start_time.replace("T", " ")}</td>
                    <td>{data.end_time}</td>
                    <td>{data.postal_code}</td>
                    <td>
                      <a href={data.url}>{data.url}</a>
                    </td>

                    <td>
                      <Link
                        to="#"
                        className="me-3 text-primary"
                        onClick={() => {
                          setEditEventData([
                            {
                              id: data.id,
                              title: data.title,
                              location: data.location,
                              event: data.event_type,
                              start: data.start_time,
                              end: data.end_time,
                              post: data.postal_code,
                              url: data.url,
                              details: data.details,
                            },
                          ]);
                          setStateValue(2);
                        }}
                      >
                        <i class="ri-pencil-line"></i>
                      </Link>
                      <Link
                        to="#"
                        className="text-danger"
                        onClick={deleteEvent}
                      >
                        <i class="ri-delete-bin-line"></i>
                      </Link>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : null}
      {stateValue === 1 ? (
        <div className="input-add-events">
          <Container fluid>
            <Row>
              <Col xs={12}>
                <Card>
                  <CardBody>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-text-input"
                        className="col-md-2 col-form-label"
                      >
                        Title*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            title === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Title"
                          }
                          id="title"
                          onChange={(e) => setTitle(e.target.value)}
                          style={
                            title === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Upload Images*
                      </Label>
                      <Col md={10}>
                        <div className="input-group">
                          <input
                            type="file"
                            className="form-control"
                            id="customFile"
                            onChange={fileSelectHandler}
                          />
                        </div>
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label
                        htmlFor="example-search-input"
                        className="col-md-2 col-form-label"
                      >
                        Address*
                      </Label>
                      <Col md={10}>
                        <Input
                          type="text"
                          placeholder={
                            address === "" && fieldsFilled === false
                              ? "This is a required field"
                              : "Enter Address"
                          }
                          id="title"
                          onChange={(e) => setAddress(e.target.value)}
                          style={
                            address === "" && fieldsFilled === false
                              ? styles
                              : null
                          }
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Label className="col-md-2 col-form-label">
                        Category*
                      </Label>
                      <Col md={10}>
                        <select
                          className="form-control"
                          onChange={(e) => setCategory(e.target.value)}
                          style={
                            (category === "" ||
                              category === "Select a Category") &&
                            fieldsFilled === false
                              ? styles
                              : null
                          }
                        >
                          <option defaultValue>Select a Category</option>
                          <option value="Resturant">Online</option>
                          <option value="Cafe">Offline</option>
                        </select>
                      </Col>
                    </Row>
