import React, { Component, useState } from "react";
import { Row, Col, Card, CardBody, Button } from "reactstrap";
import { Link } from "react-router-dom";

// datatable related plugins
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
  PaginationListStandalone,
  SizePerPageDropdownStandalone,
} from "react-bootstrap-table2-paginator";

import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";

//Import Breadcrumb
import Breadcrumbs from "../../components/Common/Breadcrumb";
import "./datatables.scss";

// Table data
const products = [
  {
    eventID: "#000",
    title: "Pet Event #1",
    startDate: "11/12/2021",
    endDate: "13/12/2021",
    location: "London",
    link: "URL",
  },

  {
    eventID: "#001",
    title: "Pet Event #1",
    startDate: "11/12/2021",
    endDate: "13/12/2021",
    location: "London",
    link: "URL",
  },
];

class PetEvents extends Component {
  constructor(props) {
    super(props);
    this.state = {
      breadcrumbItems: [
        { title: "Tables", link: "#" },
        { title: "Data Table", link: "#" },
      ],
      page: 1,
      sizePerPage: 10,
      productData: products,
      addNew: false,
      eventID: "",
      title: "",
      startDate: "",
      endDate: "",
      location: "",
      link: "",
    };
  }

  render() {
    const columns = [
      {
        dataField: "eventID",
        text: "Event ID",
        sort: true,
      },
      {
        dataField: "title",
        text: "Title",
        sort: false,
      },
      {
        dataField: "startDate",
        text: "Start Date",
        sort: true,
      },
      {
        dataField: "endDate",
        text: "End Date",
        sort: true,
      },
      {
        dataField: "location",
        text: "Location",
        sort: true,
      },
      {
        dataField: "link",
        text: "Link",
        sort: true,
      },

      {
        dataField: "menu",
        isDummyField: true,
        text: "Action",
        formatter: () => (
          <>
            <Link to="#" className="me-3 text-primary">
              <i className="mdi mdi-pencil font-size-18"></i>
            </Link>
            <Link to="#" className="text-danger">
              <i className="mdi mdi-trash-can font-size-18"></i>
            </Link>
          </>
        ),
      },
    ];

    const defaultSorted = [
      {
        dataField: "id",
        order: "asc",
      },
    ];

    const pageOptions = {
      sizePerPage: 10,
      totalSize: products.length, // replace later with size(customers),
      custom: true,
    };

    // Select All Button operation
    const selectRow = {
      mode: "checkbox",
    };

    const { SearchBar } = Search;

    return (
      <React.Fragment>
        <div className="page-content">
          {!this.state.addNew ? (
            <div className="container-fluid">
              <Breadcrumbs
                title="Pet Events"
                breadcrumbItems={this.state.breadcrumbItems}
              />

              <Row>
                <Col className="col-12">
                  <Card>
                    <CardBody>
                      <PaginationProvider
                        pagination={paginationFactory(pageOptions)}
                        keyField="id"
                        columns={columns}
                        data={this.state.productData}
                      >
                        {({ paginationProps, paginationTableProps }) => (
                          <ToolkitProvider
                            keyField="id"
                            columns={columns}
                            data={this.state.productData}
                            search
                          >
                            {(toolkitProps) => (
                              <React.Fragment>
                                <Row className="mb-2">
                                  <Col md="4">
                                    <div className="search-box me-2 mb-2 d-inline-block">
                                      <div className="position-relative">
                                        <SearchBar
                                          {...toolkitProps.searchProps}
                                        />
                                        <i className="search-box chat-search-box" />
                                      </div>
                                    </div>
                                  </Col>
                                  <Col sm="8">
                                    <div className="text-sm-end">
                                      <Button
                                        type="button"
                                        color="success"
                                        className="btn-rounded mb-2 me-2"
                                        onClick={() =>
                                          this.setState({ addNew: true })
                                        }
                                      >
                                        <i className="mdi mdi-plus me-1" /> Add
                                        Event
                                      </Button>
                                    </div>
                                  </Col>
                                </Row>

                                <Row>
                                  <Col xl="12">
                                    <div className="table-responsive">
                                      <BootstrapTable
                                        keyField={"id"}
                                        responsive
                                        bordered={false}
                                        striped={false}
                                        defaultSorted={defaultSorted}
                                        selectRow={selectRow}
                                        classes={
                                          "table align-middle table-nowrap"
                                        }
                                        headerWrapperClasses={"thead-light"}
                                        {...toolkitProps.baseProps}
                                        {...paginationTableProps}
                                      />
                                    </div>
                                  </Col>
                                </Row>

                                <Row className="align-items-md-center mt-30">
                                  <Col className="inner-custom-pagination d-flex">
                                    <div className="d-inline">
                                      <SizePerPageDropdownStandalone
                                        {...paginationProps}
                                      />
                                    </div>
                                    <div className="text-md-right ms-auto">
                                      <PaginationListStandalone
                                        {...paginationProps}
                                      />
                                    </div>
                                  </Col>
                                </Row>
                              </React.Fragment>
                            )}
                          </ToolkitProvider>
                        )}
                      </PaginationProvider>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </div>
          ) : (
            <div
              className="add-events"
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                flexDirection: "column",
              }}
            >
              <h1 style={{ fontFamily: "Avenir" }}>Add A New Event</h1>
              <div className="input-add-events">
                <input
                  className="add-events-input"
                  placeholder="Add Event ID"
                ></input>
              </div>
            </div>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default PetEvents;
