import React from "react";
import { Link } from "react-router-dom";

import Axios from "axios";

const ReadOnly = ({
  data,
  id,
  deleteEventStatus,
  getEditData,
  editState,
  token,
  getEditId,
  getEditTitle,
  getEditAddress,
  getStartTime,
}) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
  const apiUrl = `http://18.216.174.90:3000/api/event/${id}`;
  const deleteEvent = (e) => {
    e.preventDefault();
    Axios.delete(apiUrl, config)
      .then((res) => {
        console.log(res);
        deleteEventStatus(true);
        window.location.reload(true);
      })
      .catch((err) => deleteEventStatus(false));
  };

  return (
    <tr>
      <td>{data.title}</td>
      <td>{data.location}</td>
      <td>
        {data.event_type.charAt(0).toUpperCase() + data.event_type.slice(1)}
      </td>
      <td>{data.start_time.replace("T", " ")}</td>
      <td>{data.end_time}</td>
      <td>{data.postal_code}</td>
      <td>
        <a href={data.url}>{data.url}</a>
      </td>

      <td>
        <Link
          to="#"
          className="me-3 text-primary"
          onClick={() => {
            getEditData([
              {
                id: data.id,
                title: data.title,
                location: data.location,
                event: data.event_type,
                start: data.start_time,
                end: data.end_time,
                post: data.postal_code,
                url: data.url,
                details: data.detail,
              },
            ]);
            editState();
          }}
        >
          <i class="ri-pencil-line"></i>
        </Link>
        <Link to="#" className="text-danger" onClick={deleteEvent}>
          <i class="ri-delete-bin-line"></i>
        </Link>
      </td>
    </tr>
  );
};

export default ReadOnly;
